import groovy.sql.*
import groovy.time.TimeCategory
import java.text.SimpleDateFormat
import groovy.transform.Field


def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

def url = context.expand( '${#Project#database_url}' )
def user = context.expand( '${#Project#database_user}' )
def password = context.expand( '${#Project#database_password}' )
def driver = context.expand( '${#Project#database_driver}' )
def  con = Sql.newInstance(url,user,password, "oracle.jdbc.driver.OracleDriver");  
@Field parsedJsonOffers = null;
parsedJsonOffers = new groovy.json.JsonSlurper().parseText(context.expand( '${Call BGT Offer#Response#$[\'offers\']}' ));


// Validate offer size in both db and response
validateOfferSize(con)
validateSpecificOfferAttributes(con)
validateChargeAttributes(con) 


def validateChargeAttributes(con) {
	for(def offer : parsedJsonOffers) {
			def dbChargedetails = getChargeDetails(con ,offer.offerCode ) ;
			def chargesArray = offer.charges
			 // Validate the charge data is same on both database and response
			log.info(" dbChargedetails.size() --"+dbChargedetails)
			log.info(" chargesArray.size() --"+chargesArray)
			 
		//	assert dbChargedetails.size() == chargesArray.size()
			List<String> chargesList = new ArrayList<String>();
			for(def charge :chargesArray) {
				log.info(" charge ----"+charge)
				StringBuilder dataBuilder = new StringBuilder();
				dataBuilder.append(charge.bonusMiles)
				dataBuilder.append("-")
				dataBuilder.append(charge.discountPercentage)
				dataBuilder.append("-")
				dataBuilder.append(charge.discountRate)
				dataBuilder.append("-")
				dataBuilder.append(charge.bonusMilesPercentage)
				dataBuilder.append("-")
				dataBuilder.append(charge.milesTier)
				dataBuilder.append("-")
				dataBuilder.append(charge.currency)
				dataBuilder.append("-")
				dataBuilder.append(charge.chargeAmount)
				log.info("charge data ----"+dataBuilder.toString())
				chargesList.add(dataBuilder.toString())
			}
		
		for(def chargeRow :dbChargedetails) {
		
			 dataBuilder = new StringBuilder();
			 if(chargeRow.bonusMiles == null) {
			 	chargeRow.bonusMiles =''
			 }
			dataBuilder.append(chargeRow.bonusMiles)
			dataBuilder.append("-")
		
			 if(chargeRow.discountPercentage == null) {
			 	chargeRow.discountPercentage =''
			 }
			
			dataBuilder.append(chargeRow.discountPercentage)
			dataBuilder.append("-")
		
			 if(chargeRow.discountRate == null) {
			 	chargeRow.discountRate =''
			 }
			 
			dataBuilder.append(chargeRow.discountRate)
			dataBuilder.append("-")
		
			 if(chargeRow.bonusMilesPercentage == null) {
			 	chargeRow.bonusMilesPercentage =''
			 }
			 
			dataBuilder.append(chargeRow.bonusMilesPercentage)
			dataBuilder.append("-")
			 if(chargeRow.milesTier == null) {
			 	chargeRow.milesTier =''
			 }
			
			dataBuilder.append(chargeRow.milesTier)
			dataBuilder.append("-")
		
			 if(chargeRow.currency == null) {
			 	chargeRow.currency =''
			 }
			dataBuilder.append(chargeRow.currency)
			dataBuilder.append("-")
			 if(chargeRow.chargeAmount == null) {
			 	chargeRow.chargeAmount =''
			 }
			
			dataBuilder.append(chargeRow.chargeAmount)
			log.info("---------------"+dataBuilder.toString())
		
			// Validate each row .
		
			assert chargesList.contains(dataBuilder.toString())
		}
	}
}


def validateSpecificOfferAttributes(con) {
	for(def offer : parsedJsonOffers) {
		def specificRow =getAllDetailsFromDatabaseBasedOnOfferCode(con , offer.offerCode)
		assert offer.product == specificRow.product;
		assert offer.taxable == specificRow.taxable;
		assert offer.gsthsttax == Math.floor(specificRow.gsthsttax)
		assert offer.offerType == specificRow.offerType
		assert offer.usExciseTax == specificRow.usExciseTax
		assert offer.processingCharge == specificRow.processingCharge
		assert returnEmptyIfNull(offer.prasId) == returnEmptyIfNull(specificRow.prasId)
		assert offer.offerCategory == specificRow.offerCategory
		assert offer.effectiveDate == specificRow.effectiveDate.toString().substring(0,10)
		assert offer.expirationDate == specificRow.expirationDate.toString().substring(0,10)
	}
}


def getChargeDetails(def con, def offerCode) {
		 str = new StringBuilder()
str.append(" SELECT")
str.append(" PT.PRICE_AMT bonusMiles,")
str.append(" PT.DISCNT_PERCENT discountPercentage,")
str.append(" PT.DISCNT_AMT discountRate,")
str.append(" PT.PRTNR_PRICE_AMT bonusMilesPercentage,")
str.append(" PT.SEQ_NUM milesTier,")
str.append(" PT.CURCY_CD currency,")
str.append(" PT.COST_PER_PT chargeAmount")
str.append(" FROM SIEBEL.S_PROD_INT SP ,SIEBEL.S_LOY_PROD_PT PT where")
str.append(" SP.ROW_ID = PT.PROD_ID AND")
str.append(" SP.NAME = '$offerCode'")
str.append(" and PT.COST_PER_PT is not null")

 rows = con.rows(str.toString())
 return rows
}



private returnEmptyIfNull(def value) {
	if(value == null ) {
		return '';
	}
	return value;
}

private def validateOfferSize(def con) {
	def dbAllOffers  = getAllRowsFromDatabase(con);
		// Validate the DB size and response size are same.
	assert dbAllOffers.size() == parsedJsonOffers.size()
}

def  getAllDetailsFromDatabaseBasedOnOfferCode(def  con, offerCode) {
StringBuilder str = new StringBuilder()
	str.append(" SELECT")
	str.append(" PROD_CD product,")
	str.append(" X_ADV_TAXABLE_FLG taxable,")
	str.append(" ORDER_CST gsthsttax,")
	str.append(" PROD_ATTRIB01_CD offerType,")
	str.append(" NAME offerCode,")
	str.append(" APRX_COST_AMT usExciseTax,")
	str.append(" PROD_ATTRIB03_CD discountType,")
	str.append(" UNITS_INVENT processingCharge,")
	str.append(" X_PRAS_ID prasId,")
	str.append(" CATEGORY_CD offerCategory,")
	str.append(" EFF_START_DT effectiveDate,")
	str.append(" EFF_END_DT expirationDate")
	str.append(" FROM SIEBEL.S_PROD_INT SP  where BUILD = 'Active'")
	str.append(" AND EFF_START_DT <= SYSDATE AND EFF_END_DT >= SYSDATE and NAME ='$offerCode'")

	def row = con.firstRow(str.toString())
	return row;
}




def  getAllRowsFromDatabase(def  con) {
StringBuilder str = new StringBuilder()
	str.append(" SELECT")
	str.append(" PROD_CD product,")
	str.append(" X_ADV_TAXABLE_FLG taxable,")
	str.append(" ORDER_CST gsthsttax,")
	str.append(" PROD_ATTRIB01_CD offerType,")
	str.append(" NAME offerCode,")
	str.append(" APRX_COST_AMT usExciseTax,")
	str.append(" PROD_ATTRIB03_CD discountType,")
	str.append(" UNITS_INVENT processingCharge,")
	str.append(" X_PRAS_ID prasId,")
	str.append(" CATEGORY_CD offerCategory,")
	str.append(" EFF_START_DT effectiveDate,")
	str.append(" EFF_END_DT expirationDate")
	str.append(" FROM SIEBEL.S_PROD_INT SP  where BUILD = 'Active'")
	str.append(" AND EFF_START_DT <= SYSDATE AND EFF_END_DT >= SYSDATE")

	def rows = con.rows(str.toString())
	return rows;
}




con.close()

