import groovy.sql.* import groovy.time.TimeCategory import java.text.SimpleDateFormat
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def url = context.expand( '${#Project#database_url}' ) def user = context.expand( '${#Project#database_user}' ) def password = context.expand( '${#Project#database_password}' ) def driver = context.expand( '${#Project#database_driver}' )
def con = Sql.newInstance(url,user,password, "oracle.jdbc.driver.OracleDriver");
def advantageNumber = context.expand( '${#TestCase#fromAdvantageNumber}' )
StringBuilder str = new StringBuilder() str.append(" SELECT") str.append(" OFFER_CODE,") str.append(" PRODUCT_TYPE,") str.append(" OFFER_STATUS,") str.append(" OFFER_CATEGORY,") str.append(" OFFER_START_DATE,") str.append(" OFFER_END_DATE,") str.append(" OFFER_PRECED,") str.append(" MEM_NUM,") str.append(" MEM_TAC,") str.append(" MEM_TAC_ASSOC,") str.append(" OFR_TAC_ASSOC,") str.append(" dbms_random.string('A', 15) AS ROW_ID,") str.append(" 0 AS CONFLICT_ID,") str.append(" 0 AS MODIFICATION_NUM,") str.append(" SYSDATE AS CREATED,") str.append(" 0 AS CREATED_BY,") str.append(" SYSDATE AS LAST_UPD,") str.append(" SYSDATE AS DB_LAST_UPD,") str.append(" 'Object Manager - Default' AS DB_LAST_UPD_SRC,") str.append(" 0 AS LAST_UPD_BY") str.append(" FROM") str.append(" (") str.append(" SELECT") str.append(" OFR.NAME AS OFFER_CODE,") str.append(" OFR.PROD_CD AS PRODUCT_TYPE,") str.append(" OFR.BUILD AS OFFER_STATUS,") str.append(" OFR.CATEGORY_CD AS OFFER_CATEGORY,") str.append(" OFR.EFF_START_DT AS OFFER_START_DATE,") str.append(" OFR.EFF_END_DT AS OFFER_END_DATE,") str.append(" CASE WHEN OFR.PROD_CD = 'Buy-Gift' THEN '1' ELSE '0' END AS OFFER_PRECED,") str.append(" MEM.MEM_NUM AS MEM_NUM,") str.append(" BGTAC.NAME AS MEM_TAC,") str.append(" MEMTAC.CREATED AS MEM_TAC_ASSOC,") str.append(" OFTAC.CREATED AS OFR_TAC_ASSOC") str.append(" FROM SIEBEL.S_CAMP_CON MEMTAC,SIEBEL.S_SRC BGTAC,SIEBEL.S_PROD_INT OFR,SIEBEL.S_PROD_INT_XM OFTAC,SIEBEL.S_LOY_MEMBER MEM") str.append(" WHERE") str.append(" MEMTAC.SRC_ID = BGTAC.ROW_ID AND OFR.ROW_ID = OFTAC.PAR_ROW_ID AND OFTAC.ATTRIB_03 = MEMTAC.SRC_ID AND BGTAC.CUST_TRGT_TYPE_CD = 'ADV_BGT_TYPE'") str.append(" AND OFR.CATEGORY_CD ='Promotional'") str.append(" AND OFR.BUILD = 'Active'") str.append(" AND OFR.EFF_START_DT <= SYSDATE AND OFR.EFF_END_DT >= SYSDATE") str.append(" AND MEM.PR_CON_ID = MEMTAC.CON_PER_ID") str.append(" AND MEM.MEM_NUM = '$advantageNumber'") str.append(" UNION") str.append(" SELECT") str.append(" OFR.NAME AS OFFER_CODE,") str.append(" OFR.PROD_CD AS PRODUCT_TYPE,") str.append(" OFR.BUILD AS OFFER_STATUS,") str.append(" OFR.CATEGORY_CD AS OFFER_CATEGORY,") str.append(" OFR.EFF_START_DT AS OFFER_START_DATE,") str.append(" OFR.EFF_END_DT AS OFFER_END_DATE,") str.append(" CASE WHEN OFR.PROD_CD = 'Buy-Gift' THEN '1' ELSE '0' END AS OFFER_PRECED,") str.append(" '' AS MEM_NUM,") str.append(" '' AS MEM_TAC,") str.append(" NULL AS MEM_TAC_ASSOC,") str.append(" NULL AS OFR_TAC_ASSOC") str.append(" FROM SIEBEL.S_PROD_INT OFR") str.append(" WHERE OFR.CATEGORY_CD in ('Standard','Promotional')") str.append(" AND OFR.BUILD = 'Active'") str.append(" AND OFR.EFF_START_DT <= SYSDATE AND OFR.EFF_END_DT >= SYSDATE") str.append(" AND OFR.ROW_ID NOT IN (SELECT OFTAC.PAR_ROW_ID FROM SIEBEL.S_PROD_INT_XM OFTAC)") str.append(" ) ORDER BY MEM_TAC_ASSOC DESC")
def rows =con.rows(str.toString())
List<String> offerCodeList = new ArrayList<String>(); for(def row: rows) { offerCodeList.add(row.OFFER_CODE.toString()) }
def response_offers = context.expand( '${Call BGT Offer#Response#$[\'offers\']}' ) def parsedJson = new groovy.json.JsonSlurper().parseText(response_offers);
def offerCode ;
for(def offer : parsedJson) {
offerCode = offer.offerCode; // Checking whether the offer code in response is present in database. assert offerCodeList.contains(offer.offerCode.toString())
// Get all details based on the offercode.
str = new StringBuilder() str.append(" SELECT") str.append(" PROD_CD product,") str.append(" X_ADV_TAXABLE_FLG taxable,") str.append(" ORDER_CST gsthsttax,") str.append(" PROD_ATTRIB01_CD offerType,") str.append(" NAME offerCode,") str.append(" APRX_COST_AMT usExciseTax,") str.append(" PROD_ATTRIB03_CD discountType,") str.append(" UNITS_INVENT processingCharge,") str.append(" X_PRAS_ID prasId,") str.append(" CATEGORY_CD offerCategory,") str.append(" EFF_START_DT effectiveDate,") str.append(" EFF_END_DT expirationDate") str.append(" FROM SIEBEL.S_PROD_INT SP where SP.NAME = '$offerCode'")
log.info(str.toString())
def row = con.firstRow(str.toString())
// Compare the response details with the details on the database. assert offer.product == row.product assert offer.taxable == row.taxable if(row.gsthsttax != null ) { assert offer.gsthsttax == Math.floor(row.gsthsttax) } else { assert offer.gsthsttax == '' } assert offer.offerType == row.offerType assert offer.usExciseTax == row.usExciseTax assert offer.processingCharge == row.processingCharge if(row.prasId != null ) { assert offer.prasId == row.prasId } assert offer.offerCategory == row.offerCategory assert offer.effectiveDate == row.effectiveDate.toString().substring(0,10) assert offer.expirationDate == row.expirationDate.toString().substring(0,10)
}
con.close()

// generate random flight no
 def randomNumberGenerator = { String alphabet, int n ->
  new Random().with {
    (1..n).collect { alphabet[ nextInt( alphabet.length() ) ] }.join()
  }
}
def productPrice = randomNumberGenerator( (('1'..'9')).join(), 1 )
def baseMiles = randomNumberGenerator( (('1'..'9')).join(), 2 )
def bonusMiles = randomNumberGenerator( (('1'..'9')).join(), 1 )
def processingFee = randomNumberGenerator( (('1'..'9')).join(), 2 )
def taxUSExcise =randomNumberGenerator( (('1'..'9')).join(), 2 )+"."+randomNumberGenerator( (('1'..'9')).join(), 2 )
def taxGSTHST =randomNumberGenerator( (('1'..'9')).join(), 2 )+"."+randomNumberGenerator( (('1'..'9')).join(), 2 )
def miscAmount =randomNumberGenerator( (('1'..'9')).join(), 2 )+"."+randomNumberGenerator( (('1'..'9')).join(), 2 )
def totalAmount =randomNumberGenerator( (('1'..'9')).join(), 2 )+"."+randomNumberGenerator( (('1'..'9')).join(), 2 )


testRunner.testCase.setPropertyValue("productPrice",productPrice)
testRunner.testCase.setPropertyValue("baseMiles",baseMiles)
testRunner.testCase.setPropertyValue("bonusMiles",bonusMiles)
testRunner.testCase.setPropertyValue("taxUSExcise",taxUSExcise)
testRunner.testCase.setPropertyValue("taxGSTHST",taxGSTHST)
testRunner.testCase.setPropertyValue("miscAmount",miscAmount)
testRunner.testCase.setPropertyValue("totalAmount",totalAmount)
testRunner.testCase.setPropertyValue("processingFee",processingFee)
testRunner.testCase.setPropertyValue("currency","USD")

