assert row.sub_type_cd == 'Transfer'
assert row.type_cd == 'Redemption'
assert row.Amt_val == Integer.parseInt(context.expand( '${#TestCase#productPrice}' ))
assert row.loan_balance == Integer.parseInt(context.expand( '${#TestCase#baseMiles}' ))
