def response_transactionNum = context.expand( '${BGT Fulfillment Request#Response#$[\'transactionNum\']}' )
def response_loyaltyTransactionId = context.expand( '${BGT Fulfillment Request#Response#$[\'loyaltyTransactionId\']}' )

testRunner.testCase.setPropertyValue("response_transactionNum",response_transactionNum);
testRunner.testCase.setPropertyValue("response_loyaltyTransactionId",response_loyaltyTransactionId);

def response = context.expand( '${Execute Member Transaction#Response#declare namespace loy=\'http://www.ventana.com/LoyaltyMemberTransactionService/\'; //loy:RetrieveMemberTransactionListResponse[1]}' )
def response_code = context.expand( '${Execute Member Transaction#Response#declare namespace loy=\'http://www.ventana.com/LoyaltyMemberTransactionService/\'; //loy:RetrieveMemberTransactionListResponse[1]/RetrieveMemberTransactionListResponseStatus[1]/Code[1]}' )
def response_message = context.expand( '${Execute Member Transaction#Response#declare namespace loy=\'http://www.ventana.com/LoyaltyMemberTransactionService/\'; //loy:RetrieveMemberTransactionListResponse[1]/RetrieveMemberTransactionListResponseStatus[1]/Message[1]}' )

assert response_code == 'I0001'
assert response_message == 'Successful Execution'


// Populate activity date as today's date
def now = new Date();
def nowStr = now.format("yyyy-MM-dd");


def baseMiles = context.expand( '${#TestCase#baseMiles}' )
log.info baseMiles
	

def retrieveMemberTransactionListResponse = new XmlSlurper().parseText(response)
def transactionSummaryInfoArray = retrieveMemberTransactionListResponse.TransactionSummary.'*'.findAll { node ->
    node.name() == 'TransactionSummaryInfo' && node.ActivityType =='Transfer' && node.ActivityDate ==nowStr  && node.EarnedMileages.TotalAwardEligibleMiles == "-$baseMiles"
}


assert transactionSummaryInfoArray.size() != 0

def baseMiles = context.expand( '${#TestCase#baseMiles}' )
def bonusMiles = context.expand( '${#TestCase#bonusMiles}' )


// Validate from Account miles

def fromAccount_currentMiles = context.expand( '${Execute Ext Member Call Again - fromMember#Response#declare namespace adv2=\'http://www.siebel.com/xml/ADVBGSMemberProfileInfoResponse\'; declare namespace adv=\'http://siebel.com/loyalty/ADVBGSMemberService\'; //adv:LoyaltyMemberProfile_Output[1]/adv2:ExtMemberProfileInfoResponse[1]/Ext_LoyaltyMemberProfileResponse[1]/AwardMilesBalance[1]}' )
def from_member_miles_before_test = context.expand( '${#TestCase#from_member_miles_before_test}' )



assert (Integer.parseInt(baseMiles)-Integer.parseInt(bonusMiles))	 == Integer.parseInt(from_member_miles_before_test) - Integer.parseInt(fromAccount_currentMiles)

// validate to Account Miles

def toAccount_currentMiles = context.expand( '${Execute Ext Member Call Again - toMember#Response#declare namespace adv2=\'http://www.siebel.com/xml/ADVBGSMemberProfileInfoResponse\'; declare namespace adv=\'http://siebel.com/loyalty/ADVBGSMemberService\'; //adv:LoyaltyMemberProfile_Output[1]/adv2:ExtMemberProfileInfoResponse[1]/Ext_LoyaltyMemberProfileResponse[1]/AwardMilesBalance[1]}' )
def to_member_miles_before_test = context.expand( '${#TestCase#to_member_miles_before_test}' )


assert Integer.parseInt(baseMiles)	 == Integer.parseInt(toAccount_currentMiles) - Integer.parseInt(to_member_miles_before_test)

