def user = context.expand( '${#Project#database_user}' )
def password = context.expand( '${#Project#database_password}' )
def driver = context.expand( '${#Project#database_driver}' )

def  con = Sql.newInstance(url,user,password, "oracle.jdbc.driver.OracleDriver");  

def response_loyaltyTransactionId = context.expand( '${BGT Fulfillment Request#Response#$[\'loyaltyTransactionId\']}' )


 
StringBuilder str = new StringBuilder("")
str.append("select status_cd,sub_type_cd,type_cd,amt_val,loan_balance,num_points,opty_cost,order_qty,orig_amount,comments,CURCY_CD,integration_id ");
str.append("  FROM SIEBEL.S_LOY_TXN WHERE  row_id ='$response_loyaltyTransactionId' ");

