import groovy.sql.*
import groovy.time.TimeCategory

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

def url = context.expand( '${#Project#database_url}' )
def user = context.expand( '${#Project#database_user}' )
def password = context.expand( '${#Project#database_password}' )
def driver = context.expand( '${#Project#database_driver}' )

def  con = Sql.newInstance(url,user,password, "oracle.jdbc.driver.OracleDriver");  

def advantageNumber = context.expand( '${#TestCase#advantageNumber}' )
def memberStatus = context.expand( '${DataSource#memberStatus}' )


validateTierStatus(advantageNumber,con)
validateNames(advantageNumber,con)
validateSecurityStatus(advantageNumber,con)
validateAwardMilesAndBGT(advantageNumber,con)

// validate Tier status

def validateTierStatus(def advantageNumber,def con) {
	def response_eliteLevel = context.expand( '${Execute BGT Profile Rest call#Response#$[\'eliteLevel\']}' )
	def tierStatus = context.expand( '${DataSource#tierStatus}' )
	assert response_eliteLevel ==tierStatus

}



def validateNames(def advantageNumber,def  con ) {
	// validate firstName and last Name
	StringBuilder queryBuilder = new StringBuilder("")
	queryBuilder.append("select  l.mem_num , c.last_name, c.fst_name, c.MID_NAME,c.per_title,c.per_title_suffix,l.status_cd , l.X_VESTED_PLATINUM, l.X_VESTED_GOLD , l.START_DT , l.LAST_TXN_PROCED_DT , l.LFTM_PT_TYPE_E_VAL");
	queryBuilder.append(" from SIEBEL.S_Loy_Member l , siebel.S_CONTACT c where l.mem_num='");
	queryBuilder.append(advantageNumber);
	queryBuilder.append("' and c.row_id =l.PR_CON_ID");
	
	def query1_result = con.rows(queryBuilder.toString())
	
	def response_firstName = context.expand( '${Execute BGT Profile Rest call#Response#$[\'firstName\']}' )
	def response_lastName = context.expand( '${Execute BGT Profile Rest call#Response#$[\'lastName\']}' )
	
	assert response_lastName == query1_result.last_name[0]
	assert response_firstName == query1_result.fst_name[0]

}


// validate security status code 

def validateSecurityStatus(def advantageNumber,def  con ) {
	StringBuilder securityStatusQuery = new StringBuilder("")
	securityStatusQuery.append("SELECT X_ADV_DISPLAY_VALUE as SecurityStatusdisplayValue")
	securityStatusQuery.append(" FROM siebel.s_lst_of_val")
	securityStatusQuery.append(" WHERE type ='ADV_SEC_CODE_STATUS'")
	securityStatusQuery.append(" AND X_ADV_DISPLAY_VALUE IN ( ")
	securityStatusQuery.append(" SELECT ATTRIB_45")
	securityStatusQuery.append(" FROM siebel.s_loy_member_xm ")
	securityStatusQuery.append(" WHERE type  ='Security Codes' ")
	securityStatusQuery.append(" AND row_id IN ")
	securityStatusQuery.append(" (SELECT X_ADV_SEC_ID FROM siebel.s_loy_member WHERE mem_num ='")
	securityStatusQuery.append(advantageNumber)
	securityStatusQuery.append("'))")

	
	def db_security_result = con.rows(securityStatusQuery.toString().trim())

	def response_securityStatusCode = context.expand( '${Execute BGT Profile Rest call#Response#$[\'securityRestriction\']}' )
	
	
	if(db_security_result.size()  != 0 && response_securityStatusCode != '<SecurityStatusCode/>') {
		assert db_security_result.SecurityStatusdisplayValue[0] == response_securityStatusCode
	} else {
		// no need to do assert because of no value
	}

}




def validateAwardMilesAndBGT(def advantageNumber,def  con) {

	def response_awardMilesBalance = context.expand( '${Execute BGT Profile Rest call#Response#$[\'aemBalance\']}' )
	def response_bgtAmount = context.expand( '${Execute BGT Profile Rest call#Response#$[\'buyGiftAmt\']}' )
	def response_transferFromAmount = context.expand( '${Execute BGT Profile Rest call#Response#$[\'transferFromAmt\']}' )
	def response_transferToAmount = context.expand( '${Execute BGT Profile Rest call#Response#$[\'transferToAmt\']}' )


	StringBuilder awardMilesQuery = new StringBuilder("")
	awardMilesQuery.append(" select a.POINT_TYPE_F_VAL as AwardMilesBalance from siebel.S_LOY_MEMBER a where ")
	awardMilesQuery.append("  a.mem_num ='$advantageNumber'")
	def awardMilesQueryResponse = con.rows(awardMilesQuery.toString())
	assert response_awardMilesBalance.toString() == awardMilesQueryResponse.AwardMilesBalance[0].toString()

	




	StringBuilder bgtQuery = new StringBuilder("")
	bgtQuery.append(" select a.mem_num, c.display_name, b.value from siebel.S_LOY_MEMBER a, siebel.S_LOY_ATTRIB b, siebel.S_LOY_ATTRDEFN c where  ")
	bgtQuery.append(" a.row_id = b.member_id")
	bgtQuery.append(" and c.row_id = b.attrib_defn_id")
	bgtQuery.append(" and c.DISPLAY_NAME in ('BuyGiftAmt','TransferFromAmt','TransferToAmt')")
	bgtQuery.append(" and a.mem_num ='$advantageNumber'")

	
	def bgtQueryResponseArray = con.rows(bgtQuery.toString())

	for(def row : bgtQueryResponseArray) {
		if(row.display_name == 'BuyGiftAmt') {
			assert response_bgtAmount == row.value
		} else if (row.display_name == 'TransferFromAmt') {
			assert response_transferFromAmount == row.value
		}else if (row.display_name == 'TransferToAmt') {
			log.info(" response_transferToAmount "+response_transferToAmount)
			log.info(" row.value[0] "+row.value)
			assert response_transferToAmount == row.value
		}
	}
 	
}

con.close()

