// Import the Groovy class required to work with SQL databases.
import groovy.sql.*
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

// Set up database connection properties.



def url = context.expand( '${#Project#database_url}' )
def user = context.expand( '${#Project#database_user}' )
def password = context.expand( '${#Project#database_password}' )
def driver = context.expand( '${#Project#database_driver}' )

def  con = Sql.newInstance(url,user,password, "oracle.jdbc.driver.OracleDriver");  

def advantageNumber = context.expand( '${#TestCase#advantageNumber}' )

 void assertResponseValueWithDBValue(def valueType, def responseValue,def dbValue) {
	if(dbValue != null && responseValue != '') {
		log.info("valueType --- "+valueType+" dbValue ---  "+dbValue+" responseValue ----"+responseValue)
		assert responseValue.toString() == dbValue.toString()
	}
}

StringBuilder str = new StringBuilder("")
str.append(" select row_id  FROM SIEBEL.S_Loy_member where mem_num ='$advantageNumber' ");

log.info("query1"+ str.toString())
def query1_result = con.firstRow(str.toString())
def member_id =query1_result.row_id

str = new StringBuilder("")
str.append("select num_points,internal_origin,internal_dest,ext_res_num,mktg_flt_num,oper_flt_num,book_class_cd,reason_cd,fare_type_cd,x_base_EQM,X_adv_EQM_SUM,X_ADV_EQS_SUM,X_PREV_LPT_E_VAL,");
str.append(" X_PREV_PT_F_VAL,X_TOTAL_EQM,X_TOTAL_AEM,X_TOTAL_MM_MILES,X_BILL_BASE_AEM,X_BILL_BONUS_AEM FROM SIEBEL.S_LOY_TXN WHERE    member_id='$member_id' and  X_ACTIVITY_TYPE ='Airline' and txn_dt > sysdate -180");
log.info("query2"+ str.toString())
def query2_result = con.rows(str.toString())

def response = context.expand( '${Execute Member Transaction#Response#declare namespace loy=\'http://www.ventana.com/LoyaltyMemberTransactionService/\'; //loy:RetrieveMemberTransactionListResponse[1]}' )
def retrieveMemberTransactionListResponse = new XmlSlurper().parseText(response);
def transactionSummaryArray = retrieveMemberTransactionListResponse.'*'.findAll { node ->
    node.name() == 'TransactionSummary' 
}

// DB length and response transactionSummary should match
//assert transactionSummaryArray.size() == query2_result.size()

for( def transactionSummary: transactionSummaryArray) {

	def transactionDetailsArray = transactionSummary.TransactionDetailsInfo;

	for(def transactionDetails : transactionDetailsArray) {
		def recordLocator = transactionSummary.TransactionSummaryInfo.PNRNumber;
		def ticketNumber = transactionSummary.TransactionSummaryInfo.TicketNumber;
		def origin = transactionDetails.TransactionInfo.Origin;
		def destination = transactionDetails.TransactionInfo.Destination;
		def flightNum = transactionDetails.TransactionInfo.FlightNumber;

		if(ticketNumber != '') {
			ticketNumber = ticketNumber.toString().substring(3)
		}
		str = new StringBuilder("")
		str.append("select txn_DT, PROCESS_DT,POST_DT,num_points,orig_airport_cd,dest_airport_cd,internal_origin,internal_dest,ext_res_num,mktg_flt_num,oper_flt_num,book_class_cd,reason_cd,fare_type_cd,x_base_EQM,X_adv_EQM_SUM,X_ADV_EQS_SUM,X_PREV_LPT_E_VAL,");
		str.append(" X_PREV_PT_F_VAL,X_TOTAL_EQM,X_TOTAL_AEM,X_TOTAL_MM_MILES,X_BILL_BASE_AEM,X_BILL_BONUS_AEM, X_CLASS_OF_SERVICE,X_TXN_DESC  FROM SIEBEL.S_LOY_TXN WHERE    member_id='$member_id' and  X_ACTIVITY_TYPE ='Airline' and txn_dt > sysdate -180");
		if(recordLocator != '') {
			str.append(" and ext_res_num = '$recordLocator'  ")
		}
		if(ticketNumber != '') {
			str.append(" and X_PAR_CNJ_TCKT ='$ticketNumber' ")
		}
		str.append(" and orig_airport_cd='$origin' and dest_airport_cd ='$destination' ")
		str.append(" and MKTG_FLT_NUM =$flightNum")
		log.info(" query3 "+str.toString())
		
		
		def query3_result = con.firstRow(str.toString())	

		assert transactionDetails.TransactionInfo.TransactionType == "Flight"
		assert transactionDetails.TransactionInfo.ActivityType == "Airline"
		log.info("transactionDetails.TransactionInfo.PostedDate ---"+transactionDetails.TransactionInfo.PostedDate)
		log.info("transactionDetails.TransactionInfo.ActivityDate ---"+transactionDetails.TransactionInfo.ActivityDate)
		//log.info("query3_result.POST_DT ---"+query3_result.POST_DT)
		//log.info("query3_result.PROCESS_DT ---"+query3_result.PROCESS_DT)
		//log.info("query3_result.txn_DT ---"+query3_result.txn_DT)
		
		assertResponseValueWithDBValue('transactionDetails.TransactionInfo.PostedDate',transactionDetails.TransactionInfo.PostedDate,query3_result.PROCESS_DT.toString().substring(0,10))
		assertResponseValueWithDBValue('transactionDetails.TransactionInfo.PNRNumber',transactionDetails.TransactionInfo.PNRNumber,query3_result.ext_res_num)
		assertResponseValueWithDBValue('transactionDetails.TransactionInfo.Origin',transactionDetails.TransactionInfo.Origin,query3_result.orig_airport_cd)
		assertResponseValueWithDBValue('transactionDetails.TransactionInfo.Destination',transactionDetails.TransactionInfo.Destination,query3_result.dest_airport_cd)
		assertResponseValueWithDBValue('transactionDetails.TransactionInfo.FlightNumber',transactionDetails.TransactionInfo.FlightNumber,query3_result.mktg_flt_num)
		assertResponseValueWithDBValue('transactionDetails.TransactionInfo.BookingClassCode',transactionDetails.TransactionInfo.BookingClassCode,query3_result.book_class_cd)
		assertResponseValueWithDBValue('transactionDetails.TransactionInfo.ClassofServiceCode',transactionDetails.TransactionInfo.ClassofServiceCode,query3_result.X_CLASS_OF_SERVICE)
		assertResponseValueWithDBValue('transactionDetails.TransactionInfo.FlightNumber',transactionDetails.TransactionInfo.FlightNumber,query3_result.mktg_flt_num)
		assertResponseValueWithDBValue('transactionDetails.TransactionInfo.AccrualReason',transactionDetails.TransactionInfo.AccrualReason,query3_result.reason_cd)
		assertResponseValueWithDBValue('transactionDetails.TransactionInfo.FlightMiles',transactionDetails.TransactionInfo.FlightMiles,query3_result.NUM_POINTS)
		assertResponseValueWithDBValue('transactionDetails.TransactionInfo.TransactionDescription',transactionDetails.TransactionInfo.TransactionDescription,query3_result.X_TXN_DESC)

		//assertResponseValueWithDBValue('transactionDetails.TransactionInfo.EarnedMileages.TotalAwardEligibleMiles',transactionDetails.TransactionInfo.EarnedMileages.TotalAwardEligibleMiles,query3_result.X_TOTAL_AEM)
		assertResponseValueWithDBValue('transactionDetails.TransactionInfo.EarnedMileages.BaseAwardEligibleMiles',transactionDetails.TransactionInfo.EarnedMileages.BaseAwardEligibleMiles,query3_result.X_BILL_BASE_AEM)
		assertResponseValueWithDBValue('transactionDetails.TransactionInfo.EarnedMileages.COSBonusMiles',transactionDetails.TransactionInfo.EarnedMileages.COSBonusMiles,query3_result.X_BILL_BONUS_AEM)
		
	}

}

con.close()

