// Import the Groovy class required to work with SQL databases.
import groovy.sql.*
import java.text.*;
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

// Set up database connection properties.



def url = context.expand( '${#Project#database_url}' )
def user = context.expand( '${#Project#database_user}' )
def password = context.expand( '${#Project#database_password}' )
def driver = context.expand( '${#Project#database_driver}' )

def  con = Sql.newInstance(url,user,password, "oracle.jdbc.driver.OracleDriver");  

def advantageNumber = context.expand( '${#TestCase#advantageNumber}' )



StringBuilder str = new StringBuilder("")
str.append(" select row_id  FROM SIEBEL.S_Loy_member where mem_num ='$advantageNumber' ");

def query1_result = con.firstRow(str.toString())
def member_id =query1_result.row_id


def query2_result = con.rows(str.toString())



def response = context.expand( '${Execute Member Transaction#Response#declare namespace loy=\'http://www.ventana.com/LoyaltyMemberTransactionService/\'; //loy:RetrieveMemberTransactionListResponse[1]}' )
def retrieveMemberTransactionListResponse = new XmlSlurper().parseText(response);
def transactionSummaryArray = retrieveMemberTransactionListResponse.'*'.findAll { node ->
    node.name() == 'TransactionSummary' 
}

for(def transactionSummary : transactionSummaryArray) {
	log.info(" transactionSummary ---"+transactionSummary)

	def totalEliteQualMiles = context.expand( '${Execute Member Transaction#Response#declare namespace loy=\'http://www.ventana.com/LoyaltyMemberTransactionService/\'; //loy:RetrieveMemberTransactionListResponse[1]/TransactionSummary[1]/TransactionSummaryInfo[1]/EarnedMileages[1]/TotalEliteQualifyingSegments[1]}' )
	def activityDateJson = context.expand( '${Execute Member Transaction#Response#declare namespace loy=\'http://www.ventana.com/LoyaltyMemberTransactionService/\'; //loy:RetrieveMemberTransactionListResponse[1]/TransactionSummary[1]/TransactionSummaryInfo[1]/ActivityDate[1]}' )
	def postedDateJson = context.expand( '${Execute Member Transaction#Response#declare namespace loy=\'http://www.ventana.com/LoyaltyMemberTransactionService/\'; //loy:RetrieveMemberTransactionListResponse[1]/TransactionSummary[1]/TransactionSummaryInfo[1]/PostedDate[1]}' )
     def totalAwardEligibleMiles = context.expand( '${Execute Member Transaction#Response#declare namespace loy=\'http://www.ventana.com/LoyaltyMemberTransactionService/\'; //loy:RetrieveMemberTransactionListResponse[1]/TransactionSummary[1]/TransactionSummaryInfo[1]/EarnedMileages[1]/TotalAwardEligibleMiles[1]}' )

	
	def activityDate = new SimpleDateFormat("yyyy-MM-dd").parse(activityDateJson)
	def activityDateQueryFormat = new SimpleDateFormat("dd-MMM-YY").format(activityDate)

	def postedDate = new SimpleDateFormat("yyyy-MM-dd").parse(postedDateJson)
	def postedDateQueryFormat = new SimpleDateFormat("dd-MMM-YY").format(postedDate)

	def activityType = transactionSummary.TransactionSummaryInfo.ActivityType;
	log.info("activityType ---"+activityType)
	
	str = new StringBuilder("")
	str.append("select PNR_NAME,X_ACTIVITY_TYPE,txn_dt,process_dt,X_SUPPLIED_MILES,x_nap_bonus,(select OU_NUM from siebel.s_org_ext where row_id = partner_Id) partner, ");
	str.append(" x_base_EQM,X_adv_EQM_SUM,X_ADV_EQS_SUM,X_PREV_LPT_E_VAL, X_PREV_PT_F_VAL,X_TOTAL_EQM,X_TOTAL_AEM,X_TOTAL_MM_MILES, X_BILL_BASE_AEM,X_BILL_BONUS_AEM FROM SIEBEL.S_LOY_TXN WHERE  member_id='$member_id' and ")
	str.append("  status_cd !='Cancelled' and sub_type_cd !='Cancellation' and X_ACTIVITY_TYPE not in ('Airline','Award Issuance') and txn_dt > sysdate -180");
	str.append(" and X_total_AEM = $totalAwardEligibleMiles  ")
	str.append(" and TXN_DT ='$activityDateQueryFormat'   and X_ACTIVITY_TYPE='$activityType' ")
//and process_DT='$postedDateQueryFormat'
	def query3_result = con.firstRow(str.toString())
	log.info(str.toString())
	log.info(query3_result)

	assert transactionSummary.TransactionSummaryInfo.TransactionType == "Non-Flight"
	assert transactionSummary.TransactionSummaryInfo.ActivityType == query3_result.X_Activity_type
	assert postedDateJson== query3_result.process_dt.toString().substring(0,10)
	if(query3_result.PNR_NAME != null) {
		assert transactionSummary.TransactionSummaryInfo.NameonCertificate== query3_result.PNR_NAME
	}
	assert transactionSummary.TransactionSummaryInfo.MarketingPartnerCode== query3_result.partner

	assert transactionSummary.TransactionDetailsInfo.TransactionInfo.TransactionType == "Non-Flight"
	assert transactionSummary.TransactionDetailsInfo.TransactionInfo.ActivityType == query3_result.X_Activity_type
	assert postedDateJson== query3_result.process_dt.toString().substring(0,10)
	if(query3_result.PNR_NAME != null) {
		assert transactionSummary.TransactionDetailsInfo.TransactionInfo.NameonCertificate== query3_result.PNR_NAME
	}
	assert transactionSummary.TransactionDetailsInfo.TransactionInfo.MarketingPartnerCode== query3_result.partner

}


con.close()

