// Import the Groovy class required to work with SQL databases.
import groovy.sql.*
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
import java.text.*;
import groovy.transform.Field


// Set up database connection properties.



def url = context.expand( '${#Project#database_url}' )
def user = context.expand( '${#Project#database_user}' )
def password = context.expand( '${#Project#database_password}' )
def driver = context.expand( '${#Project#database_driver}' )

def  con = Sql.newInstance(url,user,password, "oracle.jdbc.driver.OracleDriver");  

def advantageNumber = context.expand( '${#TestCase#advantageNumber}' )


StringBuilder str = new StringBuilder("")
str.append("  select row_id  from SIEBEL.S_LOY_MEMBER where mem_num='$advantageNumber' ")
def query1_result = con.firstRow(str.toString())

def member_id = query1_result.row_id;

 void assertResponseValueWithDBValue( def responseValue,def dbValue) {
	if(dbValue != null && responseValue != '') {
		log.info("dbValue"+dbValue)
		log.info("responseValue"+responseValue)
		assert responseValue.toString() == dbValue.toString()
	}
}



	def retrieveMemberTransactionListResponseAsJson = context.expand( '${Execute Member Transaction#Response#declare namespace loy=\'http://www.ventana.com/LoyaltyMemberTransactionService/\'; //loy:RetrieveMemberTransactionListResponse[1]}' )
	def retrieveMemberTransactionListResponse = new XmlSlurper().parseText(retrieveMemberTransactionListResponseAsJson);
	def transactionSummaryArray = retrieveMemberTransactionListResponse.'*'.findAll { node ->
	    node.name() == 'TransactionSummary' 
	}
	int count =0;
	for(def transactionSummary : transactionSummaryArray) {

	def  transactionDetailsInfoArray = transactionSummary.TransactionDetailsInfo;

	log.info("transactionDetailsInfoArray ---"+transactionDetailsInfoArray.size())

	count++;
		def certificateNumber = transactionSummary.TransactionSummaryInfo.AwardCetificateNumber
		def transactionType = transactionSummary.TransactionSummaryInfo.TransactionType
		String awardType = transactionSummary.TransactionSummaryInfo.AwardType
		log.info("transactionType --- count "+transactionType+"---- "+count+++"awardType "+awardType)
		def db_TransactionType = null;
		
		if(transactionType == 'Award' && awardType.endsWith("AIRLINE AWARD") && transactionDetailsInfoArray.size() == 1) {
			db_TransactionType = 'IS'
			
		str = new StringBuilder("")
		str.append(" select MKTG_FLT_NUM, EXT_RES_NUM,ORIG_AIRPORT_CD,DEST_AIRPORT_CD,X_CERT_STATUS,X_TXN_DESC,STATUS_CD,TXN_DT,SUB_TYPE_CD,TYPE_CD,NUM_POINTS,PROCESS_DT,SUB_STATUS_CD,X_ACTIVITY_TYPE,X_ADV_AWARD_EXP_DT ,");
		str.append(" DB_LAST_UPD ,X_RESPONSE_DATE, X_HDLN_CODE_DESC, X_REINSTATE_DATE from SIEBEL.S_LOY_TXN where  ");
		str.append("  member_id='$member_id' and X_ACTIVITY_TYPE  in ('Award Issuance') and X_CERT_STATUS ='$db_TransactionType'");
		str.append(" and  X_certif_number ='$certificateNumber' ");
	
		log.info("str.append ---"+str.toString())
		def query2_result = con.firstRow(str.toString())
		log.info("query2_result ---"+query2_result)

		if(query2_result != null ) {
		
		assert transactionSummary.TransactionSummaryInfo.ActivityType =="Award Issuance"
	
		def activityDate =  null
	
		// Validate activityDate 
			SimpleDateFormat sdf = new SimpleDateFormat("YYYY-MM-dd")
			def activityDateStr = sdf.format(query2_result.TXN_DT)
			assert transactionSummary.TransactionSummaryInfo.ActivityDate == activityDateStr
			//assert transactionSummary.TransactionSummaryInfo.TotalAwardEligibleMiles == query2_result.NUM_POINTS+"";
			//assert transactionSummary.TransactionSummaryInfo.TransactionDescription == query2_result.X_TXN_DESC;

			log.info("transactionSummary.TransactionSummaryInfo.EarnedMileages.TotalAwardEligibleMiles ---"+transactionSummary.TransactionSummaryInfo.EarnedMileages.TotalAwardEligibleMiles)
			log.info("query2_result.NUM_POINTS ---"+query2_result.NUM_POINTS)

			
			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.BaseAwardEligibleMiles,query2_result.NUM_POINTS)
			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.TotalAwardEligibleMiles,query2_result.NUM_POINTS)
			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.PNRNumber,query2_result.EXT_RES_NUM)
			
			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.PNRNumber,query2_result.EXT_RES_NUM)
			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.Origin,query2_result.ORIG_AIRPORT_CD)

			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.Destination,query2_result.DEST_AIRPORT_CD)
			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.FlightNumber,query2_result.MKTG_FLT_NUM)

			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.ActivityDate,query2_result.TXN_DT.toString().substring(0,10))
			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.AwardExpirationDate,query2_result.X_ADV_AWARD_EXP_DT.toString().substring(0,10))
			
		   
		     // Validate TransactionDetails information

		     assertResponseValueWithDBValue(transactionSummary.TransactionDetailsInfo.TransactionInfo.PNRNumber,query2_result.EXT_RES_NUM)
			assertResponseValueWithDBValue(transactionSummary.TransactionDetailsInfo.TransactionInfo.Origin,query2_result.ORIG_AIRPORT_CD)
			assertResponseValueWithDBValue(transactionSummary.TransactionDetailsInfo.TransactionInfo.Destination,query2_result.DEST_AIRPORT_CD)
			assertResponseValueWithDBValue(transactionSummary.TransactionDetailsInfo.TransactionInfo.FlightNumber,query2_result.MKTG_FLT_NUM)
			assertResponseValueWithDBValue(transactionSummary.TransactionDetailsInfo.TransactionInfo.ActivityDate,query2_result.TXN_DT.toString().substring(0,10))
			assertResponseValueWithDBValue(transactionSummary.TransactionDetailsInfo.TransactionInfo.AwardExpirationDate,query2_result.X_ADV_AWARD_EXP_DT.toString().substring(0,10))
			assertResponseValueWithDBValue(transactionSummary.TransactionDetailsInfo.TransactionInfo.TotalAwardEligibleMiles,query2_result.NUM_POINTS)
			assertResponseValueWithDBValue(transactionSummary.TransactionDetailsInfo.TransactionInfo.BaseAwardEligibleMiles,query2_result.NUM_POINTS)
			}
		} else if(transactionType == 'Award' && awardType.endsWith("AIRLINE AWARD") && transactionDetailsInfoArray.size() > 1) {

				long totalMiles =0L;
		
				for(def transactionDetailsInfo : transactionDetailsInfoArray ) {
					// No clue why it returns RS even if it is success and not reinstatement
				 	db_TransactionType = 'RS'
				 	def origin = transactionDetailsInfo.TransactionInfo.Origin
				 	def destination =transactionDetailsInfo.TransactionInfo.Destination 
	
				 	log.info("origin ---"+origin+"----"+destination)
						
					str = new StringBuilder("")
					str.append(" select MKTG_FLT_NUM, EXT_RES_NUM,ORIG_AIRPORT_CD,DEST_AIRPORT_CD,X_CERT_STATUS,X_TXN_DESC,STATUS_CD,TXN_DT,SUB_TYPE_CD,TYPE_CD,NUM_POINTS,PROCESS_DT,SUB_STATUS_CD,X_ACTIVITY_TYPE,X_ADV_AWARD_EXP_DT ,");
					str.append(" DB_LAST_UPD ,X_RESPONSE_DATE, X_HDLN_CODE_DESC, X_REINSTATE_DATE from SIEBEL.S_LOY_TXN where  ");
					str.append("  member_id='$member_id' and X_ACTIVITY_TYPE  in ('Award Issuance') and X_CERT_STATUS  in ('RS','IS')")
					if(origin != '' || destination != '' ) {
						str.append(" and ORIG_AIRPORT_CD ='$origin' and  DEST_AIRPORT_CD ='$destination'");
					}
					str.append(" and  X_certif_number ='$certificateNumber' ");
				
					log.info("str.append ---"+str.toString())
					def query2_result = con.firstRow(str.toString())
					log.info("query2_result ---"+query2_result)
	
	
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.PNRNumber,query2_result.EXT_RES_NUM)
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.Origin,query2_result.ORIG_AIRPORT_CD)
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.Destination,query2_result.DEST_AIRPORT_CD)
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.FlightNumber,query2_result.MKTG_FLT_NUM)
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.ActivityDate,query2_result.TXN_DT.toString().substring(0,10))
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.AwardExpirationDate,query2_result.X_ADV_AWARD_EXP_DT.toString().substring(0,10));
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.BaseAwardEligibleMiles,query2_result.NUM_POINTS)
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.EarnedMileages.TotalAwardEligibleMiles,"-"+query2_result.NUM_POINTS)
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.EarnedMileages.BaseAwardEligibleMiles,"-"+query2_result.NUM_POINTS)
					totalMiles = totalMiles +query2_result.NUM_POINTS;
		}
			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.EarnedMileages.TotalAwardEligibleMiles,"-"+totalMiles)
			// Need to check with Syed on how to comapre summary for mutliple details.
	}
}

con.close()

// Import the Groovy class required to work with SQL databases.
import groovy.sql.*
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
import java.text.*;
import groovy.transform.Field


// Set up database connection properties.



def url = context.expand( '${#Project#database_url}' )
def user = context.expand( '${#Project#database_user}' )
def password = context.expand( '${#Project#database_password}' )
def driver = context.expand( '${#Project#database_driver}' )
def  con = Sql.newInstance(url,user,password, "oracle.jdbc.driver.OracleDriver");  

void assertResponseValueWithDBValue( def responseValue,def dbValue) {
	if(dbValue != null && responseValue != '') {
		log.info("dbValue"+dbValue)
		log.info("responseValue"+responseValue)
		assert responseValue.toString() == dbValue.toString()
	}
}

def advantageNumber = context.expand( '${#TestCase#advantageNumber}' )


StringBuilder str = new StringBuilder("")
str.append("  select row_id  from SIEBEL.S_LOY_MEMBER where mem_num='$advantageNumber' ")
def query1_result = con.firstRow(str.toString())

def member_id = query1_result.row_id;



	def retrieveMemberTransactionListResponseAsJson = context.expand( '${Execute Member Transaction#Response#declare namespace loy=\'http://www.ventana.com/LoyaltyMemberTransactionService/\'; //loy:RetrieveMemberTransactionListResponse[1]}' )
	def retrieveMemberTransactionListResponse = new XmlSlurper().parseText(retrieveMemberTransactionListResponseAsJson);
	def transactionSummaryArray = retrieveMemberTransactionListResponse.'*'.findAll { node ->
	    node.name() == 'TransactionSummary' 
	}
	int count =0;
	for(def transactionSummary : transactionSummaryArray) {

	def  transactionDetailsInfoArray = transactionSummary.TransactionDetailsInfo;

	log.info("transactionDetailsInfoArray ---"+transactionDetailsInfoArray.size())

	count++;
		def certificateNumber = transactionSummary.TransactionSummaryInfo.AwardCetificateNumber
		def transactionType = transactionSummary.TransactionSummaryInfo.TransactionType
		String awardType = transactionSummary.TransactionSummaryInfo.AwardType
		log.info("transactionType --- count "+transactionType+"---- "+count+++"awardType "+awardType)
		def db_TransactionType = null;
		
		if(transactionType == 'Award Reinstatement' && awardType.endsWith("AIRLINE AWARD") && transactionDetailsInfoArray.size() == 1) {
			db_TransactionType = 'RS'
			
		str = new StringBuilder("")
		str.append(" select MKTG_FLT_NUM, EXT_RES_NUM,ORIG_AIRPORT_CD,DEST_AIRPORT_CD,X_CERT_STATUS,X_TXN_DESC,STATUS_CD,TXN_DT,SUB_TYPE_CD,TYPE_CD,NUM_POINTS,PROCESS_DT,SUB_STATUS_CD,X_ACTIVITY_TYPE,X_ADV_AWARD_EXP_DT ,");
		str.append(" DB_LAST_UPD ,X_RESPONSE_DATE, X_HDLN_CODE_DESC, X_REINSTATE_DATE from SIEBEL.S_LOY_TXN where  ");
		str.append("  member_id='$member_id' and X_ACTIVITY_TYPE  in ('Award Issuance') and X_CERT_STATUS ='$db_TransactionType'");
		str.append(" and  X_certif_number ='$certificateNumber' ");
	
		log.info("str.append ---"+str.toString())
		def query2_result = con.firstRow(str.toString())
		log.info("query2_result ---"+query2_result)

		if(query2_result != null ) {
		
		assert transactionSummary.TransactionSummaryInfo.ActivityType =="Award Issuance"
	
		def activityDate =  null
	
		// Validate activityDate 
			SimpleDateFormat sdf = new SimpleDateFormat("YYYY-MM-dd")
			def activityDateStr = sdf.format(query2_result.PROCESS_DT)
			assert transactionSummary.TransactionSummaryInfo.ActivityDate == activityDateStr
			//assert transactionSummary.TransactionSummaryInfo.TotalAwardEligibleMiles == query2_result.NUM_POINTS+"";
			//assert transactionSummary.TransactionSummaryInfo.TransactionDescription == query2_result.X_TXN_DESC;

			log.info("transactionSummary.TransactionSummaryInfo.EarnedMileages.TotalAwardEligibleMiles ---"+transactionSummary.TransactionSummaryInfo.EarnedMileages.TotalAwardEligibleMiles)
			log.info("query2_result.NUM_POINTS ---"+query2_result.NUM_POINTS)

			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.BaseAwardEligibleMiles,query2_result.NUM_POINTS)
			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.TotalAwardEligibleMiles,query2_result.NUM_POINTS)
			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.PNRNumber,query2_result.EXT_RES_NUM)
			
			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.PNRNumber,query2_result.EXT_RES_NUM)
			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.Origin,query2_result.ORIG_AIRPORT_CD)

			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.Destination,query2_result.DEST_AIRPORT_CD)
			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.FlightNumber,query2_result.MKTG_FLT_NUM)

			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.ActivityDate,query2_result.PROCESS_DT.toString().substring(0,10))
			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.AwardExpirationDate,query2_result.X_ADV_AWARD_EXP_DT.toString().substring(0,10))    // Validate TransactionDetails information

		    // Validate TransactionDetails information

		     assertResponseValueWithDBValue(transactionSummary.TransactionDetailsInfo.TransactionInfo.PNRNumber,query2_result.EXT_RES_NUM)
			assertResponseValueWithDBValue(transactionSummary.TransactionDetailsInfo.TransactionInfo.Origin,query2_result.ORIG_AIRPORT_CD)
			assertResponseValueWithDBValue(transactionSummary.TransactionDetailsInfo.TransactionInfo.Destination,query2_result.DEST_AIRPORT_CD)
			assertResponseValueWithDBValue(transactionSummary.TransactionDetailsInfo.TransactionInfo.FlightNumber,query2_result.MKTG_FLT_NUM)
			assertResponseValueWithDBValue(transactionSummary.TransactionDetailsInfo.TransactionInfo.ActivityDate,query2_result.PROCESS_DT.toString().substring(0,10))
			assertResponseValueWithDBValue(transactionSummary.TransactionDetailsInfo.TransactionInfo.AwardExpirationDate,query2_result.X_ADV_AWARD_EXP_DT.toString().substring(0,10))
			assertResponseValueWithDBValue(transactionSummary.TransactionDetailsInfo.TransactionInfo.TotalAwardEligibleMiles,query2_result.NUM_POINTS)
			assertResponseValueWithDBValue(transactionSummary.TransactionDetailsInfo.TransactionInfo.BaseAwardEligibleMiles,query2_result.NUM_POINTS)
	
			}
		} else if(transactionType == 'Award Reinstatement' && awardType.endsWith("AIRLINE AWARD") && transactionDetailsInfoArray.size() > 1) {

				long totalMiles =0L;
		
				for(def transactionDetailsInfo : transactionDetailsInfoArray ) {
					// No clue why it returns RS even if it is success and not reinstatement
				 	db_TransactionType = 'RS'
				 	def origin = transactionDetailsInfo.TransactionInfo.Origin
				 	def destination =transactionDetailsInfo.TransactionInfo.Destination 
	
				 	log.info("origin ---"+origin+"----"+destination)
						
					str = new StringBuilder("")
					str.append(" select MKTG_FLT_NUM, EXT_RES_NUM,ORIG_AIRPORT_CD,DEST_AIRPORT_CD,X_CERT_STATUS,X_TXN_DESC,STATUS_CD,TXN_DT,SUB_TYPE_CD,TYPE_CD,NUM_POINTS,PROCESS_DT,SUB_STATUS_CD,X_ACTIVITY_TYPE,X_ADV_AWARD_EXP_DT ,");
					str.append(" DB_LAST_UPD ,X_RESPONSE_DATE, X_HDLN_CODE_DESC, X_REINSTATE_DATE from SIEBEL.S_LOY_TXN where  ");
					str.append("  member_id='$member_id' and X_ACTIVITY_TYPE  in ('Award Issuance') and X_CERT_STATUS  in ('RS','IS') and ORIG_AIRPORT_CD ='$origin' and  DEST_AIRPORT_CD ='$destination'");
					str.append(" and  X_certif_number ='$certificateNumber' ");
				
					log.info("str.append ---"+str.toString())
					def query2_result = con.firstRow(str.toString())
					log.info("query2_result ---"+query2_result)


					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.PNRNumber,query2_result.EXT_RES_NUM)
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.Origin,query2_result.ORIG_AIRPORT_CD)
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.Destination,query2_result.DEST_AIRPORT_CD)
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.FlightNumber,query2_result.MKTG_FLT_NUM)
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.ActivityDate,query2_result.TXN_DT.toString().substring(0,10))
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.AwardExpirationDate,query2_result.X_ADV_AWARD_EXP_DT.toString().substring(0,10));
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.BaseAwardEligibleMiles,query2_result.NUM_POINTS)
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.EarnedMileages.TotalAwardEligibleMiles,"-"+query2_result.NUM_POINTS)
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.EarnedMileages.BaseAwardEligibleMiles,"-"+query2_result.NUM_POINTS)
	
					totalMiles = totalMiles +query2_result.NUM_POINTS;
			
		}

				// Need to check with Syed on how to comapre summary for mutliple details.
	}

	}



con.close()

