//The following two lines needs to be modified for each test case capturing the respective request and response for corresponding service call

def full_request  =  context.expand( '${Execute Member Transaction#RawRequest}' )
log.info full_request
def full_response = context.expand( '${Execute Member Transaction#Response}' )

def tierStatus = context.expand( '${DataSource#tierStatus}' )

//The following two lines is for creating unique filenames. These two needs to be changes for each test case
def req_FileName = testCaseName +tierStatus+"_Request.txt"
def resp_FileName = testCaseName +tierStatus+"_Response.txt"

def file1 = new File(suitefolder.toString() + "/" + req_FileName)
def file2 = new File(suitefolder.toString() + "/" + resp_FileName)
file1.createNewFile()
file2.createNewFile()

//for appending the contents into same file  comment the next 2 lines
file1.write(full_request)
file2.write(full_response)

def response_code = context.expand( '${Execute Member Transaction#Response#declare namespace loy=\'http://www.ventana.com/LoyaltyMemberTransactionService/\'; //loy:RetrieveMemberTransactionListResponse[1]/RetrieveMemberTransactionListResponseStatus[1]/Code[1]}' )
def response_message = context.expand( '${Execute Member Transaction#Response#declare namespace loy=\'http://www.ventana.com/LoyaltyMemberTransactionService/\'; //loy:RetrieveMemberTransactionListResponse[1]/RetrieveMemberTransactionListResponseStatus[1]/Message[1]}' )

assert response_code =="I0001"
assert response_message =="Successful Execution"

