					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.Destination,query2_result.DEST_AIRPORT_CD)
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.FlightNumber,query2_result.MKTG_FLT_NUM)
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.ActivityDate,query2_result.TXN_DT.toString().substring(0,10))
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.AwardExpirationDate,query2_result.X_ADV_AWARD_EXP_DT.toString().substring(0,10));
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.BaseAwardEligibleMiles,query2_result.NUM_POINTS)
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.EarnedMileages.TotalAwardEligibleMiles,"-"+query2_result.NUM_POINTS)
					assertResponseValueWithDBValue(transactionDetailsInfo.TransactionInfo.EarnedMileages.BaseAwardEligibleMiles,"-"+query2_result.NUM_POINTS)
					totalMiles = totalMiles +query2_result.NUM_POINTS;
		}
			assertResponseValueWithDBValue(transactionSummary.TransactionSummaryInfo.EarnedMileages.TotalAwardEligibleMiles,"-"+totalMiles)
