//The following two lines needs to be modified for each test case capturing the respective request and response for corresponding service call

def full_request  =  context.expand( '${Call Reinstate Miles#RawRequest}' )
log.info full_request
def full_response = context.expand( '${Call Reinstate Miles#Response}' )

def tierStatus = context.expand( '${DataSource#tierStatus}' )

//The following two lines is for creating unique filenames. These two needs to be changes for each test case
def req_FileName = testCaseName +tierStatus+"_Request.txt"
def resp_FileName = testCaseName +tierStatus+"_Response.txt"

def file1 = new File(suitefolder.toString() + "/" + req_FileName)
def file2 = new File(suitefolder.toString() + "/" + resp_FileName)
file1.createNewFile()
file2.createNewFile()

//for appending the contents into same file  comment the next 2 lines
file1.write(full_request)
file2.write(full_response)

def response_statusCode = context.expand( '${Call Reinstate Miles#Response#declare namespace loy=\'http://www.ventana.com/LoyaltyMileageFulfillment/\'; //loy:FulfillMileageResponse[1]/ResponseStatus[1]/Code[1]}' )
def response_statusMessage = context.expand( '${Call Reinstate Miles#Response#declare namespace loy=\'http://www.ventana.com/LoyaltyMileageFulfillment/\'; //loy:FulfillMileageResponse[1]/ResponseStatus[1]/Message[1]}' )

assert response_statusCode == "I0001"
assert response_statusMessage == "Successful Execution"


def miles = context.expand( '${#TestCase#miles}' )
def response_miles_before_test = context.expand( '${Execute Ext Member Call#Response#declare namespace adv2=\'http://www.siebel.com/xml/ADVBGSMemberProfileInfoResponse\'; declare namespace adv=\'http://siebel.com/loyalty/ADVBGSMemberService\'; //adv:LoyaltyMemberProfile_Output[1]/adv2:ExtMemberProfileInfoResponse[1]/Ext_LoyaltyMemberProfileResponse[1]/AwardMilesBalance[1]}' )
def response_miles_after_test = context.expand( '${Execute Ext Member Call - Again#Response#declare namespace adv2=\'http://www.siebel.com/xml/ADVBGSMemberProfileInfoResponse\'; declare namespace adv=\'http://siebel.com/loyalty/ADVBGSMemberService\'; //adv:LoyaltyMemberProfile_Output[1]/adv2:ExtMemberProfileInfoResponse[1]/Ext_LoyaltyMemberProfileResponse[1]/AwardMilesBalance[1]}' )



assert Integer.parseInt(miles) == Integer.parseInt(response_miles_after_test) - Integer.parseInt(response_miles_before_test)

// Import the Groovy class required to work with SQL databases.
import groovy.sql.*
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

// Set up database connection properties.



def url = context.expand( '${#Project#database_url}' )
def user = context.expand( '${#Project#database_user}' )
def password = context.expand( '${#Project#database_password}' )
def driver = context.expand( '${#Project#database_driver}' )

def  con = Sql.newInstance(url,user,password, "oracle.jdbc.driver.OracleDriver");  

def response_loyRefNum = context.expand( '${Call Reinstate Miles#Response#declare namespace loy=\'http://www.ventana.com/LoyaltyMileageFulfillment/\'; //loy:FulfillMileageResponse[1]/FulfillMileageResult[1]/LoyRefNum[1]}' )

def miles_input = context.expand( '${#TestCase#miles}' )




StringBuilder str = new StringBuilder("")
str.append(" select status_cd , sub_type_cd , type_cd ,num_points , dest_airport_cd , ext_res_num ,orig_airport_cd , partition_name,pnr_name ,sub_status_cd, ");
str.append("  X_activity_type ,X_Adv_award_user_name ,X_cert_status ,X_wb_id,X_ACT_created_by     FROM SIEBEL.S_LOY_TXN WHERE row_id ='$response_loyRefNum'");

log.info(str.toString())

def query1_result = con.firstRow(str.toString())

assert query1_result.status_cd == "Processed"
assert query1_result.sub_type_cd == "Reactivation"
assert query1_result.type_cd == "Service"
assert query1_result.sub_status_cd == "Success"
assert query1_result.X_activity_type == "Unexpire Miles"
assert query1_result.num_points.toString() == miles_input.toString()



con.close()

