con.close();

// generate random flight no
 def randomNumberGenerator = { String alphabet, int n ->
  new Random().with {
    (1..n).collect { alphabet[ nextInt( alphabet.length() ) ] }.join()
  }
}
def miles = randomNumberGenerator( (('1'..'9')).join(), 4 )

testRunner.testCase.setPropertyValue("milesQuantity",miles)

def randomString = { String alphabet, int n ->
  new Random().with {
    (1..n).collect { alphabet[ nextInt( alphabet.length() ) ] }.join()
  }
}
def line1  = randomString( (('A'..'Z')).join(), 15 )
def line2 = randomString( (('A'..'Z')).join(), 14 )
def line3 = randomString( (('A'..'Z')).join(), 18 )

testRunner.testCase.setPropertyValue("line1",line1)
testRunner.testCase.setPropertyValue("line2",line2)
testRunner.testCase.setPropertyValue("line3",line3)

String lastName = testRunner.testCase.getPropertyValue("lastName")
log.info("lastName --->>>"+lastName)
if(lastName.size() > 20 ) {
//	lastName = lastName.substring(0,22)
	testRunner.testCase.setPropertyValue("lastName",lastName)
}

log.info("lastName ---"+lastName)


// Generate random values

def now = new Date();
def nowStr = now.format("yyyy-MM-dd");


Date randomDate(Range<Date> range) {
    range.from + new Random().nextInt(range.to - range.from + 1)
}

def start = Date.parse('yyyy-MM-dd', '2015-01-01')
def end   = Date.parse('yyyy-MM-dd', nowStr)
def  randomD = randomDate(start..end);

nowStr = randomD.format("yyyy-MM-dd");
testRunner.testCase.setPropertyValue("activityDate", nowStr)
log.info(nowStr)

