import java.text.*;
testRunner.testCase.properties.each { key,value -> testRunner.testCase.removeProperty(key) }
// generate random recordlocator
def recordLocatorGenerator = { String alphabet, int n -> new Random().with { (1..n).collect { alphabet[ nextInt( alphabet.length() ) ] }.join() } } def randomRecordLocator = recordLocatorGenerator( (('A'..'Z')).join(), 6 ) testRunner.testCase.setPropertyValue("randomRecordLocator", randomRecordLocator)
def randomTicketNumber = recordLocatorGenerator( (('1'..'9')).join(), 10 ) testRunner.testCase.setPropertyValue("randomTicketNumber", randomTicketNumber)
def randomFlightNumber = recordLocatorGenerator( (('1'..'9')).join(), 4 ) testRunner.testCase.setPropertyValue("randomFlightNumber", randomFlightNumber)
int randomNumber = Integer.parseInt(recordLocatorGenerator( (('1'..'9')).join(), 2 )) randomNumber = randomNumber *3 Date bookDate = new Date().plus(-randomNumber) Date issueDate = new Date().plus(-(randomNumber-2)) Date originalIssueDate = new Date().plus(-(randomNumber-2))
SimpleDateFormat sdf = new SimpleDateFormat("MM/dd/YYYY");
testRunner.testCase.setPropertyValue("bookDate", sdf.format(bookDate)) testRunner.testCase.setPropertyValue("issueDate", sdf.format(issueDate)) testRunner.testCase.setPropertyValue("originalIssueDate", sdf.format(originalIssueDate))
def MAX_SIZE = 6 def originArray = new Object[MAX_SIZE] originArray[0] ="AUS" originArray[1] ="DFW" originArray[2] ="ORD" originArray[3] ="JFK" originArray[4] ="ORD" originArray[5] ="DFW"
def destinationArray = new Object[MAX_SIZE]
destinationArray[0] ="DFW" destinationArray[1] ="ORD" destinationArray[2] ="JFK" destinationArray[3] ="ORD" destinationArray[4] ="DFW" destinationArray[5] ="AUS"
for(int i=0;i<6;i++) { testRunner.testCase.setPropertyValue("origin_"+(i+1),""+originArray[i]) testRunner.testCase.setPropertyValue("destination_"+(i+1),""+destinationArray[i]) testRunner.testCase.setPropertyValue("originalIssueDate", sdf.format(originalIssueDate))
Date departureDate = bookDate.plus((((i+2)*2))) testRunner.testCase.setPropertyValue("departureDate_"+(i+1), sdf.format(departureDate)) testRunner.testCase.setPropertyValue("BookingCode_"+(i+1), "N")
}

