import groovy.sql.*
import java.text.*;
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)


def url = context.expand( '${#Project#database_url}' )
def user = context.expand( '${#Project#database_user}' )
def password = context.expand( '${#Project#database_password}' )
def driver = context.expand( '${#Project#database_driver}' )
def  con = Sql.newInstance(url,user,password, "oracle.jdbc.driver.OracleDriver");  

def advantageNumber = context.expand( '${#TestCase#advantageNumber}' )


StringBuilder str = new StringBuilder("")
str.append(" select row_id  FROM SIEBEL.S_Loy_member where mem_num ='$advantageNumber' ");

log.info(str.toString())
def query1_result = con.firstRow(str.toString())
def member_id =query1_result.row_id

def postMissingMilesRequestAsXML = context.expand( '${Post Missing Miles SOAP Request#RawRequest#declare namespace pos=\'http://www.ventana.com/PostMissingMiles/\'; //pos:PostMissingMilesRequest[1]/PostMissingMilesRequestItem[1]}' )
def postMissingMilesRequest = new XmlSlurper().parseText(postMissingMilesRequestAsXML)

def segmentArray = postMissingMilesRequest.PassengerInfo.TicketInfo.ListOfSegments.'*'.findAll { node ->
   	node.name='SegmentInfo'  
}
def ticketNumber = context.expand( '${Post Missing Miles SOAP Request#RawRequest#declare namespace pos=\'http://www.ventana.com/PostMissingMiles/\'; //pos:PostMissingMilesRequest[1]/PostMissingMilesRequestItem[1]/PassengerInfo[1]/TicketInfo[1]/OriginalTicketNumber[1]}' )
def airlineCode = context.expand( '${Post Missing Miles SOAP Request#RawRequest#declare namespace pos=\'http://www.ventana.com/PostMissingMiles/\'; //pos:PostMissingMilesRequest[1]/PostMissingMilesRequestItem[1]/PassengerInfo[1]/TicketInfo[1]/TicketingAirline[1]}' )
def airlineTicketNumber = airlineCode + ticketNumber;
def recordLocator = context.expand( '${Post Missing Miles SOAP Request#RawRequest#declare namespace pos=\'http://www.ventana.com/PostMissingMiles/\'; //pos:PostMissingMilesRequest[1]/PostMissingMilesRequestItem[1]/PassengerInfo[1]/TicketInfo[1]/PNR[1]}' )


	for(def segment : segmentArray) {

		def origin = segment.Origin;
		def destination = segment.Destination;

		
		Date currentDate = new Date();
		SimpleDateFormat sdf = new SimpleDateFormat("dd-MMM-YY")
		def currentDateStr = sdf.format(currentDate)

		str = new StringBuilder("")
		str.append("select status_cd ,sub_type_cd,sub_status_cd ,num_points,internal_origin,internal_dest,ext_res_num,mktg_flt_num,oper_flt_num,book_class_cd,reason_cd,fare_type_cd,x_base_EQM,X_adv_EQM_SUM,X_ADV_EQS_SUM,X_PREV_LPT_E_VAL,");
		str.append(" X_PREV_PT_F_VAL,X_TOTAL_EQM,X_TOTAL_AEM,X_TOTAL_MM_MILES,X_BILL_BASE_AEM,X_BILL_BONUS_AEM FROM SIEBEL.S_LOY_TXN WHERE    member_id='$member_id' ");
		str.append(" and trunc(last_upd) ='$currentDateStr' and ticket_num='$airlineTicketNumber' and dest_airport_cd='$destination' and orig_airport_cd='$origin'");
		log.info(str.toString())
		
		def query2_result = con.firstRow(str.toString())
		
		assert query2_result.num_points != null
		assert query2_result.ext_res_num == recordLocator
		assert query2_result.status_cd == 'Processed'
		assert query2_result.sub_type_cd == 'Product'
		assert query2_result.sub_status_cd == 'Success'
		assert query2_result.mktg_flt_num == context.expand( '${#TestCase#randomFlightNumber}' )
		assert query2_result.oper_flt_num == context.expand( '${#TestCase#randomFlightNumber}' )
		def bookingCode = context.expand( '${Post Missing Miles SOAP Request#RawRequest#declare namespace pos=\'http://www.ventana.com/PostMissingMiles/\'; //pos:PostMissingMilesRequest[1]/PostMissingMilesRequestItem[1]/PassengerInfo[1]/TicketInfo[1]/ListOfSegments[1]/SegmentInfo[1]/BookingCode[1]}' )
		assert query2_result.book_class_cd == bookingCode
		
		//Processed	Product
	}

con.close();

