testRunner.testCase.setPropertyValue("bookDate", sdf.format(bookDate)) testRunner.testCase.setPropertyValue("issueDate", sdf.format(issueDate)) testRunner.testCase.setPropertyValue("originalIssueDate", sdf.format(originalIssueDate))
def MAX_SIZE = 6 def originArray = new Object[MAX_SIZE] originArray[0] ="AUS" originArray[1] ="DFW" originArray[2] ="ORD" originArray[3] ="JFK" originArray[4] ="ORD" originArray[5] ="DFW"
def destinationArray = new Object[MAX_SIZE]
destinationArray[0] ="DFW" destinationArray[1] ="ORD" destinationArray[2] ="JFK" destinationArray[3] ="ORD" destinationArray[4] ="DFW" destinationArray[5] ="AUS"
for(int i=0;i<6;i++) { testRunner.testCase.setPropertyValue("origin_"+(i+1),""+originArray[i]) testRunner.testCase.setPropertyValue("destination_"+(i+1),""+destinationArray[i]) testRunner.testCase.setPropertyValue("originalIssueDate", sdf.format(originalIssueDate))
