		str.append(" and trunc(last_upd) ='$currentDateStr' and ticket_num='$airlineTicketNumber' and dest_airport_cd='$destination' and orig_airport_cd='$origin'");
		log.info(str.toString())
		
		def query2_result = con.firstRow(str.toString())
		
		assert query2_result.num_points != null
		assert query2_result.ext_res_num == recordLocator
		assert query2_result.status_cd == 'Processed'
		assert query2_result.sub_type_cd == 'Product'
		assert query2_result.sub_status_cd == 'Success'
		if(count == 0 ) {
			assert query2_result.X_METHOD_NAME == 'Revenue' 
		} else {
			assert query2_result.X_METHOD_NAME == 'Distance'
		}
			
		assert query2_result.mktg_flt_num == context.expand( '${#TestCase#randomFlightNumber}' )
		assert query2_result.oper_flt_num == context.expand( '${#TestCase#randomFlightNumber}' )
		def bookingCode = context.expand( '${Post Missing Miles SOAP Request#RawRequest#declare namespace pos=\'http://www.ventana.com/PostMissingMiles/\'; //pos:PostMissingMilesRequest[1]/PostMissingMilesRequestItem[1]/PassengerInfo[1]/TicketInfo[1]/ListOfSegments[1]/SegmentInfo[1]/BookingCode[1]}' )
		assert query2_result.book_class_cd == bookingCode
		
		//Processed	Product

		count++;
	}

	str = new StringBuilder("")
	str.append("select AADV_NUM,VENTANA_TXN_NUM from  SIEBSTG.STG_AA_INBOUND_TCN_SALES where ventana_txn_num is not null")
