// Import the Groovy class required to work with SQL databases.
import groovy.sql.*
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

// Set up database connection properties.
def url = context.expand( '${#Project#database_url}' )
def user = context.expand( '${#Project#database_user}' )
def password = context.expand( '${#Project#database_password}' )
def driver = context.expand( '${#Project#database_driver}' )
log.info(url+user+password+driver)

// Register the Oracle JDBC driver – required for Groovy to send requests to the database.
com.eviware.soapui.support.GroovyUtils.registerJdbcDriver( driver )

//Get Response XML node count
def MemberNumber = context.expand( '${#TestCase#MEM_NUM}' )
def xmlHolder = groovyUtils.getXmlHolder("Request#Response")
def EliteHistoryItem = xmlHolder.getDomNodes("//eliteStatusHistoryTable").size()
log.info "EliteStatusHistory node count is" + EliteHistoryItem

for(i=0;i<EliteHistoryItem;i++){

	//Actual Values from Response XML
	def EliteLevel = xmlHolder.getNodeValue('//eliteStatusHistoryTable['+(i+1)+']/level[1]')
	def EffDate = xmlHolder.getNodeValue('//eliteStatusHistoryTable['+(i+1)+']/effectiveDate[1]')		
	def EQM = xmlHolder.getNodeValue('//eliteStatusHistoryTable['+(i+1)+']/eliteQualMiles[1]')	
	def EQD = xmlHolder.getNodeValue('//eliteStatusHistoryTable['+(i+1)+']/eliteQualifyingDollars[1]')
	def EQS = xmlHolder.getNodeValue('//eliteStatusHistoryTable['+(i+1)+']/eliteQualSegments[1]')
	def ExpiryDate = xmlHolder.getNodeValue('//eliteStatusHistoryTable['+(i+1)+']/expiryDate[1]')
	log.info EliteLevel+'-'+EffDate+'-'+EQM+'-'+EQD+'-'+EQS+'-'+ExpiryDate
	EffDate = EffDate.take(10)
	ExpiryDate = ExpiryDate.take(10)
	if(EliteLevel=='Regular'){
		EliteLevel = 'C'
	}
	// Connect to the SQL instance.
	def sql = Sql.newInstance(url, user, password, driver)
	def Query = "Select M.mem_num, tier.name as EliteLevel,T.start_dt as EffDate,T.end_dt as ExpDate, T.point_type_B_val as EQM , T.point_type_C_val as EQS, T.x_point_type_i_val AS EQD from siebel.s_loy_mem_tier T ,siebel.s_loy_member M , siebel.S_LOY_TIER Tier  where M.row_id = T.member_id and T.tier_id = Tier.row_id and M.mem_num ='"+MemberNumber+"' and T.start_dt=TO_DATE('"+EffDate+"','YYYY MM DD') and T.point_type_B_val = '"+EQM+"' "
	log.info Query
	def recordset = sql.firstRow(Query)
	//log.info("DB Record count is "+ recordset.size())

	//Expected Values from DB
	def DBEliteLevel = recordset.EliteLevel	
	def DBEffDate = recordset.EffDate
	DBEffDate = DBEffDate.toString().take(10)
	def DBExpDate = recordset.ExpDate
	DBExpDate = DBExpDate.toString().take(10)
	def DBEQM = recordset.EQM
	def DBEQS = recordset.EQS
	def DBEQD = recordset.EQD	
	log.info DBEliteLevel+'-'+DBEffDate+'-'+DBExpDate+'-'+DBEQM+'-'+DBEQS+'-'+DBEQD
	log.info EliteLevel
	assert DBEliteLevel.take(1) == EliteLevel
	assert DBEffDate == EffDate
	assert DBExpDate == ExpiryDate
	assert DBEQM.toInteger() == EQM.toInteger()
	assert DBEQS.toInteger() == EQS.toInteger()
	assert DBEQD.toInteger() == EQD.toInteger()	
}

