// Import the Groovy class required to work with SQL databases.
import groovy.sql.*
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

// Set up database connection properties.
def url = context.expand( '${#Project#database_url}' )
def user = context.expand( '${#Project#database_user}' )
def password = context.expand( '${#Project#database_password}' )
def driver = context.expand( '${#Project#database_driver}' )
log.info(url+user+password+driver)

// Register the Oracle JDBC driver – required for Groovy to send requests to the database.
com.eviware.soapui.support.GroovyUtils.registerJdbcDriver( driver )

//Get Response XML node count
def MemberNumber = context.expand( '${#TestCase#MEM_NUM}' )
def xmlHolder = groovyUtils.getXmlHolder("Request#Response")
def EliteThresoldItem = xmlHolder.getDomNodes("//memberEliteThresholdStickerTable").size()
log.info "memberEliteThresholdStickerTable node count is" + EliteThresoldItem

for(i=0;i<EliteThresoldItem;i++){

	//Actual Values from Response XML
	def ActivityDate = xmlHolder.getNodeValue('//memberEliteThresholdStickerTable['+(i+1)+']/activityDate[1]')
	def Incentives = xmlHolder.getNodeValue('//memberEliteThresholdStickerTable['+(i+1)+']/incentives[1]')		
	def Quantity = xmlHolder.getNodeValue('//memberEliteThresholdStickerTable['+(i+1)+']/quantity[1]')	
	def PostedDate = xmlHolder.getNodeValue('//memberEliteThresholdStickerTable['+(i+1)+']/stmtPostedDate[1]')	
	ActivityDate = ActivityDate.take(10)
	PostedDate = PostedDate.take(10)
	log.info ActivityDate+'-'+Incentives+'-'+Quantity+'-'+PostedDate
	
	// Connect to the SQL instance.
	def sql = Sql.newInstance(url, user, password, driver)
	def Query = "SELECT M.mem_num, m.x_earned_level AS EliteLevel,S.x_adv_upgrade_type AS transTypeDesc, S.x_adv_activity_date AS Activity_Dateval, s.x_adv_posted_date AS Posted_Date, s.x_adv_earn AS EarnDBQuantity FROM Siebel.cx_500m_summary S, siebel.S_loy_member M where s.par_row_id = M.row_id and S.x_adv_upgrade_type = 'Earned' AND M.mem_num ='"+MemberNumber+"' and S.x_adv_activity_date = To_Date('"+ActivityDate+"', 'YYYY-MM-DD')   "
	log.info Query
	def recordset = sql.firstRow(Query)
	//log.info("DB Record count is "+ recordset.size())

	//Expected Values from DB
	def DBEliteLevel = recordset.EliteLevel	
	def DBIncentives = recordset.transTypeDesc	
	def DBActivityDate = recordset.Activity_Dateval
	def DBQuantity = recordset.EarnDBQuantity
	def DBPostedDate = recordset.Posted_Date
	DBActivityDate = DBActivityDate.toString().take(10)
	DBPostedDate = DBPostedDate.toString().take(10)	
	
	log.info DBEliteLevel+'-'+DBIncentives+'-'+DBActivityDate+'-'+DBQuantity+'-'+DBPostedDate
	
	//assert DBIncentives == Incentives
	assert DBActivityDate == ActivityDate	
	//assert DBPostedDate == PostedDate
	assert DBQuantity.toInteger() == Quantity.toInteger()
	log.info 'Validation of Record '+i +'Passed'
}

