// Import the Groovy class required to work with SQL databases.
import groovy.sql.*
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

// Set up database connection properties.
def url = context.expand( '${#Project#database_url}' )
def user = context.expand( '${#Project#database_user}' )
def password = context.expand( '${#Project#database_password}' )
def driver = context.expand( '${#Project#database_driver}' )
log.info(url+user+password+driver)

// Register the Oracle JDBC driver – required for Groovy to send requests to the database.
com.eviware.soapui.support.GroovyUtils.registerJdbcDriver( driver )

//Get Response XML node count
def MergedMember = context.expand( '${#TestCase#MergedMember}' )
def xmlHolder = groovyUtils.getXmlHolder("Request#Response")
def MergeChainTable = xmlHolder.getDomNodes("//mergeChainTable").size()
log.info "MergeChainTable node count is" + MergeChainTable

for(i=0;i<MergeChainTable;i++){

	//Actual Values from Response XML
	def MergeDate = xmlHolder.getNodeValue('//mergeChainTable['+(i+1)+']/mergeDate[1]')
	def FromAccount = xmlHolder.getNodeValue('//mergeChainTable['+(i+1)+']/mergeFromAadvantageNbr[1]')		
	def ToAccount = xmlHolder.getNodeValue('//mergeChainTable['+(i+1)+']/mergeToAadvantageNbr[1]')	
	MergeDate = MergeDate.take(10)
	log.info MergeDate+'-'+FromAccount+'-'+ToAccount
	
	// Connect to the SQL instance.
	def sql = Sql.newInstance(url, user, password, driver)
	def Query = "SELECT x_adv_from_accnt, x_adv_to_acnt,  x_adv_merge_date FROM siebel.cx_merge_chain WHERE x_adv_from_accnt = '"+MergedMember+"'   "
	log.info Query
	def recordset = sql.firstRow(Query)
	//log.info("DB Record count is "+ recordset.size())

	//Expected Values from DB
	def DBFromAccount = recordset.x_adv_from_accnt	
	def DBToAccount = recordset.x_adv_to_acnt	
	def DBMergeDate = recordset.x_adv_merge_date
	DBMergeDate = DBMergeDate.toString().take(10)	
	
	log.info DBFromAccount+'-'+DBToAccount+'-'+DBMergeDate
	
	assert DBMergeDate == MergeDate	
	assert DBFromAccount == FromAccount
	assert DBToAccount == ToAccount
}

