// Import the Groovy class required to work with SQL databases.
import groovy.sql.*
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

// Set up database connection properties.
def url = context.expand( '${#Project#database_url}' )
def user = context.expand( '${#Project#database_user}' )
def password = context.expand( '${#Project#database_password}' )
def driver = context.expand( '${#Project#database_driver}' )
//log.info(url+user+password+driver)

// Register the Oracle JDBC driver – required for Groovy to send requests to the database.
com.eviware.soapui.support.GroovyUtils.registerJdbcDriver( driver )

//Get Response XML node count
def MemberNumber = context.expand( '${#TestCase#MemberNumber}' )
def ReqItemCount = context.expand( '${Request#Request#declare namespace cxf=\'http://cxfws.jee.customerservices.ecdb.americanairlines.com/\'; //cxf:RetrieveMemberComments[1]/request[1]/requestedItemCount[1]}' )
def xmlHolder = groovyUtils.getXmlHolder("Request#Response")
def CommentsItemCount = xmlHolder.getDomNodes("//memberCommentsTable").size()
log.info "Comments node count is" + CommentsItemCount

//Get DB Data
// Connect to the SQL instance.
def sql = Sql.newInstance(url, user, password, driver)
def Query = "SELECT C.created, c.note_type,C.note FROM siebel.s_note_con C, siebel.s_loy_member M WHERE c.src_row_id = M.pr_con_id AND M.mem_num ='"+MemberNumber+"' order by created desc"
//log.info Query
def recordset = sql.rows(Query)
log.info recordset

assert CommentsItemCount.toInteger() == ReqItemCount.toInteger()

for(i=0;i<CommentsItemCount;i++){

	//Actual Values from Response XML		
	def CreatedDate = xmlHolder.getNodeValue('//memberCommentsTable['+(i+1)+']/creationDate[1]')
	def Comments = xmlHolder.getNodeValue('//memberCommentsTable['+(i+1)+']/memberAccountCommentFreeTextTable[1]')
	def TypeCode = xmlHolder.getNodeValue('//memberCommentsTable['+(i+1)+']/typeCode[1]')	
	CreatedDate = CreatedDate.take(10)
	log.info CreatedDate+'-'+Comments+'-'+TypeCode+'- I Value '+i
	
	//Expected Values from DB
	def DBCreatedDate = recordset[i].created
	def DBComments = recordset[i].note	
	def DBTypeCode = recordset[i].note_type
	DBCreatedDate = DBCreatedDate.toString().take(10)
	DBTypeCode = DBTypeCode.take(2)
	log.info DBCreatedDate.toString()+'-'+DBComments.toString()+'-'+DBTypeCode.toString()

	assert CreatedDate ==DBCreatedDate
	assert Comments.trim() == DBComments.trim()
	TypeCode=TypeCode.take(2)
	assert TypeCode.toString() == DBTypeCode.toString()
	log.info 'Record Number '+i+' Passed'
}

