def recordset = SqlInstance.firstRow("select m.mem_num as Mem_Num,n.fst_name as Mem_FName,n.last_name as Mem_LName,ti.name as EliteLevel from siebel.s_loy_member m,siebel.s_loy_member_xm xm,siebel.s_contact n,siebel.s_loy_mem_tier mt, siebel.s_loy_tier ti where m.pr_con_id = n.row_id and mt.tier_id = ti.row_id and m.pr_dom_tier_id = ti.row_id and m.row_id = xm.par_row_id and xm.type NOT IN('Restrictions') and rownum<=145  ORDER BY DBMS_RANDOM.VALUE")
