import groovy.sql.*
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

def url = context.expand( '${#Project#database_url}' )
def user = context.expand( '${#Project#database_user}' )
def password = context.expand( '${#Project#database_password}' )
def driver = context.expand( '${#Project#database_driver}' )

com.eviware.soapui.support.GroovyUtils.registerJdbcDriver( driver )
//=====================================================================================================================//
//Fetch data from DB for validations//

def SqlInstance = Sql.newInstance(url, user, password, driver)
def memberNum = context.expand( '${#TestCase#MemberNum}' )
log.info(memberNum)

def recordset = SqlInstance.firstRow("select  last_txn_proced_dt,point_type_f_val  from siebel.s_loy_member where mem_num='"+memberNum+"'")
log.info(recordset)
def dbLAdate=recordset.last_txn_proced_dt
def dbLAD=dbLAdate.toString().take(10)
log.info(dbLAD)

def dbAEM=recordset.point_type_f_val
dbAEM=dbAEM.toString()
log.info(dbAEM)

/*def expDate=18.months.from.dbLAD
log.info(expDate)*/

SqlInstance.close();

//=============================================================================================================//
//validate the miles to expire value in response//
def resAEM = context.expand( '${Request#Response#declare namespace ns2=\'http://cxfws.jee.customerservices.ecdb.americanairlines.com/\'; //ns2:retrieveMemberMileageActivityByYearResponse[1]/return[1]/milesToExpire[1]}' )
resAEM=resAEM.toString()
log.info(resAEM)
assert resAEM==dbAEM

//Validate the last activity date in the response//
def resLAD = context.expand( '${Request#Response#declare namespace ns2=\'http://cxfws.jee.customerservices.ecdb.americanairlines.com/\'; //ns2:retrieveMemberMileageActivityByYearResponse[1]/return[1]/lastActivity[1]}' ).toString().take(10)
log.info(resLAD) 
assert resLAD==dbLAD

//validate the mileage expiration date in the response//
def resExpdate = context.expand( '${Request#Response#declare namespace ns2=\'http://cxfws.jee.customerservices.ecdb.americanairlines.com/\'; //ns2:retrieveMemberMileageActivityByYearResponse[1]/return[1]/dateExpire[1]}' ).toString().take(10)
log.info(resExpdate)

//Validate the miles by year records //
def xmlHolder = groovyUtils.getXmlHolder("Request#Response")
def mileActivityByYearInfTable = xmlHolder.getDomNodes("//mileActivityByYearInfTable").size()
log.info "mileActivityByYearInfTable node count is" + mileActivityByYearInfTable

for(i=0;i<mileActivityByYearInfTable;i++){
//Actual Values from Response XML
def BaseMiles = xmlHolder.getNodeValue('//mileActivityByYearInfTable['+(i+1)+']/base[1]')
BaseMiles=BaseMiles.toString()
def year=xmlHolder.getNodeValue('//mileActivityByYearInfTable['+(i+1)+']/year[1]')
def BonusMiles = xmlHolder.getNodeValue('//mileActivityByYearInfTable['+(i+1)+']/bonus[1]')
BonusMiles=BonusMiles.toString()
def Claimed = xmlHolder.getNodeValue('//mileActivityByYearInfTable['+(i+1)+']/claimed[1]')
Claimed=Claimed.toString()
//def Expired = xmlHolder.getNodeValue('//mileActivityByYearInfTable['+(i+1)+']/expired[1]')
//Expired=Expired.toString()
def txnCount = xmlHolder.getNodeValue('//mileActivityByYearInfTable['+(i+1)+']/transactions[1]')
txnCount=txnCount.toString()

//Fetch the values from DB for validations//
SqlInstance = Sql.newInstance(url, user, password, driver)
recordset=SqlInstance.firstRow("select xm.attrib_14,xm.attrib_15,xm.attrib_16,xm.attrib_17,xm.attrib_21,xm.attrib_19 from siebel.s_loy_member_xm xm,siebel.s_loy_member m where xm.type='ADV_ANNUAL_HISTORY' and xm.par_row_id=m.row_id and xm.attrib_14='"+year+"'  and m.mem_num='"+memberNum+"'")
log.info(recordset)

def dbBase=recordset.attrib_15
dbBase=dbBase.toString()

def dbBonus=recordset.attrib_16
dbBonus=dbBonus.toString()

def dbClaimed=recordset.attrib_17
dbClaimed=dbClaimed.toString()

/*def dbExpired=recordset.attrib_21
dbExpired=dbExpired.toString()*/

def dbtxnCount=recordset.attrib_19
dbtxnCount=dbtxnCount.toString()

SqlInstance.close()

assert BaseMiles ==dbBase
assert BonusMiles==dbBonus
assert Claimed==dbClaimed
//assert Expired==dbExpired
assert txnCount==dbtxnCount

log.info i+"th node assertion is passed"
}

