import groovy.sql.*
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

def url = context.expand( '${#Project#database_url}' )
def user = context.expand( '${#Project#database_user}' )
def password = context.expand( '${#Project#database_password}' )
def driver = context.expand( '${#Project#database_driver}' )

com.eviware.soapui.support.GroovyUtils.registerJdbcDriver( driver )
def txnId = context.expand( '${#TestCase#TxnId}' )
def SqlInstance = Sql.newInstance(url, user, password, driver)
def recordset = SqlInstance.firstRow("select x_base_miles,x_bonus_miles,x_elite_bonus,x_supplied_miles,x_nap_bonus,x_total_AEM from siebel.s_loy_txn where row_id='"+txnId+"'")
log.info(recordset)

def BonusMiles = recordset.x_nap_bonus
BonusMiles=BonusMiles.toString()
def TotalMiles=recordset.x_total_AEM
TotalMiles=TotalMiles.toString()
SqlInstance.close();


//===================================================================================================================//
//Data validations//
def resBonusmiles = context.expand( '${Request#Response#declare namespace ns2=\'http://cxfws.jee.customerservices.ecdb.americanairlines.com/\'; //ns2:retrieveMemberActivityBonusDetailResponse[1]/return[1]/bonusMiles[1]}' )
resBonusmiles=resBonusmiles.toString()
def resTotalBonusmiles = context.expand( '${Request#Response#declare namespace ns2=\'http://cxfws.jee.customerservices.ecdb.americanairlines.com/\'; //ns2:retrieveMemberActivityBonusDetailResponse[1]/return[1]/totBonusMiles[1]}' )
resTotalBonusmiles=resTotalBonusmiles.toString()
def resTotalmiles = context.expand( '${Request#Response#declare namespace ns2=\'http://cxfws.jee.customerservices.ecdb.americanairlines.com/\'; //ns2:retrieveMemberActivityBonusDetailResponse[1]/return[1]/totMiles[1]}' )
resTotalmiles=resTotalmiles.toString()

assert BonusMiles==resBonusmiles
assert BonusMiles==resTotalBonusmiles
assert TotalMiles==resTotalmiles

def xmlHolder = groovyUtils.getXmlHolder("Request#Response")
def promoBonusTable = xmlHolder.getDomNodes("//promoBonusTable").size()
log.info "promoBonusTable node count is" + promoBonusTable

for(i=0;i<promoBonusTable;i++){
//Actual values from response xml
def adjustedMiles = xmlHolder.getNodeValue('//promoBonusTable['+(i+1)+']/adjustedMiles[1]')
adjustedMiles=adjustedMiles.toString()
def promoBonus = xmlHolder.getNodeValue('//promoBonusTable['+(i+1)+']/promoBonus[1]')
promoBonus=promoBonus.toString()
def datePosted = xmlHolder.getNodeValue('//promoBonusTable['+(i+1)+']/datePosted[1]')
datePosted=datePosted.take(10)
def respromoCode = xmlHolder.getNodeValue('//promoBonusTable['+(i+1)+']/promoCode[1]')
def respromoDesc = xmlHolder.getNodeValue('//promoBonusTable['+(i+1)+']/promoDesc[1]')

//============================================================================================================================//
//Retrieve data from accrual item table//
 SqlInstance = Sql.newInstance(url, user, password, driver)
 recordset = SqlInstance.firstRow("select accrualed_value,process_dt,p.x_adv_promo_cd,p.name from siebel.s_loy_acrl_itm ai,siebel.s_loy_promo p  where ai.txn_id='"+txnId+"' and ai.promo_id=p.row_id")
log.info(recordset)

def dbMiles=recordset.accrualed_value
dbMiles=dbMiles.toString()
SqlInstance.close();

 def processDt=recordset.process_dt
 processDt=processDt.toString().take(10)

 def promoCode=recordset.x_adv_promo_cd
 def promoDesc=recordset.name
 
assert promoBonus==dbMiles
assert datePosted==processDt
assert respromoCode==promoCode
assert respromoDesc==promoDesc

log.info i+"th node assertion is passed"
}

