def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

def url = context.expand( '${#Project#database_url}' )
def user = context.expand( '${#Project#database_user}' )
def password = context.expand( '${#Project#database_password}' )
def driver = context.expand( '${#Project#database_driver}' )

com.eviware.soapui.support.GroovyUtils.registerJdbcDriver( driver )
def reqsummaryId = context.expand( '${#TestCase#SummaryId}' )
//================================================================================================================//
//retrieve data from DB
def SqlInstance = Sql.newInstance(url, user, password, driver)
def recordset = SqlInstance.firstRow("select x_adv_complementary,x_adv_earn,x_adv_exchanged_miles,x_adv_purchased,X_ADV_UPGRADE_TYPE,x_adv_activity_date,x_adv_posted_date from siebel.cx_500m_summary where row_id='"+reqsummaryId+"'")
log.info(recordset)

def dbComplimentary= recordset.x_adv_complementary
def dbEarned=recordset.x_adv_earn
def dbExchanged=recordset.x_adv_exchanged_miles
def dbPurchased=recordset.x_adv_purchased
def dbTotal=dbComplimentary+dbEarned+dbExchanged+dbPurchased

dbComplimentary=dbComplimentary.toString()
dbEarned=dbEarned.toString()
dbExchanged=dbExchanged.toString()
dbPurchased=dbPurchased.toString()
