def dbActivitydate=recordset.x_adv_activity_date
dbActivitydate=dbActivitydate.toString().take(10)

def dbPosteddate=recordset.x_adv_posted_date
dbPosteddate=dbPosteddate.toString().take(10)

dbUpgradetype=recordset.x_adv_upgrade_type
SqlInstance.close();
//==============================================================================================================//
//Actual values from response
def resComplimentary = context.expand( '${Request#Response#declare namespace ns2=\'http://cxfws.jee.customerservices.ecdb.americanairlines.com/\'; //ns2:retrieveMemberUpgradeCouponDetailResponse[1]/return[1]/complimentaryMiles[1]}' )
def resEarned = context.expand( '${Request#Response#declare namespace ns2=\'http://cxfws.jee.customerservices.ecdb.americanairlines.com/\'; //ns2:retrieveMemberUpgradeCouponDetailResponse[1]/return[1]/earnedMiles[1]}' )
def resExchanged = context.expand( '${Request#Response#declare namespace ns2=\'http://cxfws.jee.customerservices.ecdb.americanairlines.com/\'; //ns2:retrieveMemberUpgradeCouponDetailResponse[1]/return[1]/exchangedMiles[1]}' )
def resPurchased = context.expand( '${Request#Response#declare namespace ns2=\'http://cxfws.jee.customerservices.ecdb.americanairlines.com/\'; //ns2:retrieveMemberUpgradeCouponDetailResponse[1]/return[1]/purchasedMiles[1]}' )
def resActivitydate = context.expand( '${Request#Response#declare namespace ns2=\'http://cxfws.jee.customerservices.ecdb.americanairlines.com/\'; //ns2:retrieveMemberUpgradeCouponDetailResponse[1]/return[1]/activityDate[1]}' )
resActivitydate=resActivitydate.toString().take(10)
def resPosteddate = context.expand( '${Request#Response#declare namespace ns2=\'http://cxfws.jee.customerservices.ecdb.americanairlines.com/\'; //ns2:retrieveMemberUpgradeCouponDetailResponse[1]/return[1]/postedDate[1]}' )
resPosteddate=resPosteddate.toString().take(10)
def resUpgradetype = context.expand( '${Request#Response#declare namespace ns2=\'http://cxfws.jee.customerservices.ecdb.americanairlines.com/\'; //ns2:retrieveMemberUpgradeCouponDetailResponse[1]/return[1]/upgTransTypeDesc[1]}' )
def resTotal = context.expand( '${Request#Response#declare namespace ns2=\'http://cxfws.jee.customerservices.ecdb.americanairlines.com/\'; //ns2:retrieveMemberUpgradeCouponDetailResponse[1]/return[1]/totalMiles[1]}' )
//resTotal=resTotal.toInteger()

assert dbComplimentary==resComplimentary
assert dbEarned==resEarned
assert dbExchanged==resExchanged
assert dbPurchased==resPurchased
assert dbActivitydate==resActivitydate
assert dbPosteddate==resPosteddate
assert dbUpgradetype==resUpgradetype
//assert dbTotal==resTotal
//============================================================================================================================//
//Validating the upgradeCouponTable
def xmlHolder = groovyUtils.getXmlHolder("Request#Response")
def upgradeCouponTable = xmlHolder.getDomNodes("//upgradeCouponTable").size()
log.info "upgradeCouponTable node count is " + upgradeCouponTable

for(i=0;i<upgradeCouponTable;i++){
//Actual Values from Response XML
def airlineFormNumber = xmlHolder.getNodeValue('//upgradeCouponTable['+(i+1)+']/airlineFormNumber[1]')
def airlineNumber = xmlHolder.getNodeValue('//upgradeCouponTable['+(i+1)+']/airlineNumber[1]')
def category = xmlHolder.getNodeValue('//upgradeCouponTable['+(i+1)+']/category[1]')
def couponNumber = xmlHolder.getNodeValue('//upgradeCouponTable['+(i+1)+']/couponNumber[1]')
def lastUpdated = xmlHolder.getNodeValue('//upgradeCouponTable['+(i+1)+']/lastUpdated[1]')
lastUpdated=lastUpdated.toString().take(10)
def serialNumber = xmlHolder.getNodeValue('//upgradeCouponTable['+(i+1)+']/serialNumber[1]')
def upgradeDepositDate = xmlHolder.getNodeValue('//upgradeCouponTable['+(i+1)+']/upgradeDepositDate[1]')
upgradeDepositDate=upgradeDepositDate.toString().take(10)
def upgradeStatus = xmlHolder.getNodeValue('//upgradeCouponTable['+(i+1)+']/upgradeStatus[1]')
def upgradeTypeCode = xmlHolder.getNodeValue('//upgradeCouponTable['+(i+1)+']/upgradeTypeCode[1]')
def certNumber=airlineNumber+airlineFormNumber+serialNumber+couponNumber
log.info(certNumber)

//Fetch the values from DB for validations//
SqlInstance = Sql.newInstance(url, user, password, driver)
recordset=SqlInstance.firstRow("select x_certificate_no,status_cd,x_adv_upgrade_category,X_ADV_UPGRADE_CODE,x_adv_deposit_date,db_last_upd from siebel.s_loy_mem_vchr where x_certificate_no='"+certNumber+"' and  x_adv_deposit_id='"+reqsummaryId+"'")
log.info(recordset)

def dbStatus=recordset.status_cd
def dbUpgradecategory=recordset.x_adv_upgrade_category.take(1)
def dbUpgradecode=recordset.x_adv_upgrade_code
def dbDepositdate=recordset.x_adv_deposit_date.toString().take(10)
def dbLastupddate=recordset.db_last_upd.toString().take(10)
def dbCertno=recordset.x_certificate_no

assert dbStatus==upgradeStatus
assert dbUpgradecategory==category
assert dbUpgradecode==upgradeTypeCode
assert dbDepositdate==upgradeDepositDate
assert dbLastupddate==lastUpdated
assert dbCertno==certNumber

log.info i+"th node assertion is passed"
SqlInstance.close()
}

