import groovy.sql.*
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

def url = context.expand( '${#Project#database_url}' )
def user = context.expand( '${#Project#database_user}' )
def password = context.expand( '${#Project#database_password}' )
def driver = context.expand( '${#Project#database_driver}' )
com.eviware.soapui.support.GroovyUtils.registerJdbcDriver( driver )

def xmlHolder = groovyUtils.getXmlHolder("Airline_Activity#Response")
def ActivityItem = xmlHolder.getDomNodes("//partnerActivityTable").size()
log.info "LMAT - Activity node count is" + ActivityItem

for(i=0;i<ActivityItem;i++){
	
//Actual Values from Response XML
def TxnID = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/transactionId[1]')
testRunner.testCase.testSuite.project.setPropertyValue("TxnID", TxnID )
def activityDate = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/activityDate[1]')
activityDate = activityDate.take(10)
def serviceCreditClass = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/serviceCreditClass[1]')
def baseMiles = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/baseMiles[1]')
def bonusMiles = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/bonusMiles[1]')
def Origin = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/origination[1]')
def Destin = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/destination[1]')
def Distance = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/Distance[1]')
def EliteQualifyingDollars = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/EliteQualifyingDollars[1]')
def EliteQualifyingMiles = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/EliteQualifyingMiles[1]')
def EliteQualifyingSegments = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/EliteQualifyingSegments[1]')
def AccrualMethod = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/AccrualMethod[1]')
def AccrualMethodReason = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/AccrualMethodReason[1]')
def ActivityBaseEQD = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/ActivityBaseEQD[1]')
def ActivityBaseEQM = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/ActivityBaseEQM[1]')
def ActivityBaseEQS = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/ActivityBaseEQS[1]')
def ActivityBonusEQD = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/ActivityBonusEQD[1]')
def ActivityBonusEQS = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/ActivityBonusEQS[1]')
def ActivityBonusEQM = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/ActivityBonusEQM[1]')
def EliteBonus = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/EliteBonus[1]')
def TotalBonus = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/TotalBonus[1]')
def EQDMultiplier = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/EQDMultiplier[1]')
def EQMMultiplier = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/EQMMultiplier[1]')
def EQSMultiplier = xmlHolder.getNodeValue('//partnerActivityTable['+(i+1)+']/EQSMultiplier[1]')

//Get COS/CityPair ID
def sql = Sql.newInstance(url, user, password, driver)
def Query = ("select T.x_class_of_service,T.x_city_region_id from siebel.s_loy_txn T where T.row_id='"+TxnID+"' ")
def recordset = sql.firstRow(Query)
def x_class_of_service = recordset.x_class_of_service
def x_city_region_id = recordset.x_city_region_id

// Get TXN accrual Values and Multipliers from COS/CityPair Table
sql = Sql.newInstance(url, user, password, driver)
Query = ("select T.x_class_of_service,x.attrib_24 as EQDMultiplier,x.attrib_25 AS EQMMultiplier,x.attrib_23 as EQSMultiplier,M.POINT_TYPE_F_VAL as AEM,T.x_base_miles, T.x_bonus_miles,T.x_method_name,T.X_ELITE_BONUS,T.x_base_eqm, T.x_base_eqd, T.x_base_eqs, T.X_TOTAL_EQM,T.X_TOTAL_EQD,T.X_TOTAL_EQS,T.X_MIN_MILEAGE,T.type_cd as ActivityType,txn_dt as ActivityDate, T.dest_airport_cd, T.orig_airport_cd,T.reason_cd from siebel.s_loy_txn T,siebel.s_loy_member M,siebel.s_org_ext_xm X where M.row_id=T.member_id and X.row_id=T.x_cos_id and x.type='ADV_CLASS_OF_SERVICE' and T.row_id='"+TxnID+"' ")
if(x_class_of_service== null){
	log.info 'Multipliers are taken from Citypair Override table '+x_city_region_id
	Query = ("select T.x_city_region_id,x.attrib_24 as EQDMultiplier,x.attrib_25 AS EQMMultiplier,x.attrib_23 as EQSMultiplier,M.POINT_TYPE_F_VAL as AEM,T.x_base_miles, T.x_bonus_miles,T.x_method_name,T.X_ELITE_BONUS,T.x_base_eqm, T.x_base_eqd, T.x_base_eqs, T.X_TOTAL_EQM,T.X_TOTAL_EQD,T.X_TOTAL_EQS,T.X_MIN_MILEAGE,T.type_cd as ActivityType,txn_dt as ActivityDate, T.dest_airport_cd, T.orig_airport_cd,T.reason_cd from siebel.s_loy_txn T,siebel.s_loy_member M,siebel.s_org_ext_xm X where M.row_id=T.member_id and X.row_id=T.x_city_region_id and x.type='ADV_COS_BKNGCD_OVERRIDE' and T.row_id='"+TxnID+"' ")
}
recordset = sql.firstRow(Query)
def DBAEM = recordset.AEM
def x_base_miles = recordset.x_base_miles
def x_bonus_miles = recordset.x_bonus_miles
def x_method_name = recordset.x_method_name
def X_ELITE_BONUS = recordset.X_ELITE_BONUS
def x_base_eqm = recordset.x_base_eqm
def x_base_eqd = recordset.x_base_eqd
def x_base_eqs = recordset.x_base_eqs
def X_TOTAL_EQM = recordset.X_TOTAL_EQM
def X_TOTAL_EQD = recordset.X_TOTAL_EQD
def X_TOTAL_EQS = recordset.X_TOTAL_EQS
def X_MIN_MILEAGE = recordset.X_MIN_MILEAGE
def DB_ActivityType = recordset.ActivityType
def DB_ActivityDate = recordset.ActivityDate
DB_ActivityDate = DB_ActivityDate.format('YYYY-MM-dd')
def orig_airport_cd = recordset.orig_airport_cd
def dest_airport_cd = recordset.dest_airport_cd
def reason_cd = recordset.reason_cd
def DB_EQDMultiplier = recordset.EQDMultiplier
def DB_EQMMultiplier = recordset.EQMMultiplier
def DB_EQSMultiplier = recordset.EQSMultiplier

//Framing Calculated Values
def DB_TotalBonus = X_ELITE_BONUS+x_bonus_miles
log.info 'TotalBonus is'+DB_TotalBonus

def x_Bonus_EQD = X_TOTAL_EQD - x_base_eqd
def x_Bonus_EQM = X_TOTAL_EQM - x_base_eqm
def x_Bonus_EQS = X_TOTAL_EQS - x_base_eqs
log.info 'BonusEQD,EQM,EQS'+x_Bonus_EQD+x_Bonus_EQM+x_Bonus_EQS

/*def PromoBonus = (DBAEM) - (x_base_miles + x_bonus_miles + X_MIN_MILEAGE + X_ELITE_BONUS)
log.info PromoBonus*/

//Get Txn Distance from Distance Table
sql = Sql.newInstance(url, user, password, driver)
def recordset1 = sql.firstRow("select distance from siebel.S_LOY_DISTANCE where origin_cd='"+orig_airport_cd+"' and destination_cd='"+dest_airport_cd+"'")
def DB_Distance = recordset1.distance
log.info 'Distnace is'+DB_Distance

assert DB_Distance.toInteger() == Distance.toInteger()
assert activityDate == DB_ActivityDate
assert Origin == orig_airport_cd
assert Destin == dest_airport_cd
//assert x_class_of_service == serviceCreditClass
assert baseMiles.toInteger() == x_base_miles.toInteger()
assert bonusMiles.toInteger() == x_bonus_miles.toInteger()
assert EliteQualifyingDollars.toInteger() == X_TOTAL_EQD.toInteger()
assert EliteQualifyingMiles.toInteger() == X_TOTAL_EQM.toInteger()
assert EliteQualifyingSegments+"" == X_TOTAL_EQS+""
assert AccrualMethod == x_method_name
assert AccrualMethodReason == reason_cd
assert ActivityBaseEQD.toInteger() == x_base_eqd.toInteger()
assert ActivityBaseEQM.toInteger() == x_base_eqm.toInteger()
assert ActivityBaseEQS+"" == x_base_eqs+""
assert ActivityBonusEQD.toInteger() == x_Bonus_EQD.toInteger()
assert ActivityBonusEQM.toInteger() == x_Bonus_EQM.toInteger()
assert ActivityBonusEQD.toInteger() == x_Bonus_EQS.toInteger()
assert EliteBonus.toInteger() == X_ELITE_BONUS.toInteger()
assert TotalBonus.toInteger() == DB_TotalBonus.toInteger()
assert EQDMultiplier.toString() == DB_EQDMultiplier.toString()
assert EQMMultiplier.toString() == DB_EQMMultiplier.toString()
assert EQSMultiplier.toString() == DB_EQSMultiplier.toString()

log.info 'Node no: '+i+'s Validation Status: PASSED'
}

