def EliteQualifyingMiles = xmlHolder.getNodeValue('//return[1]/ADVEliteQualifyingMiles[1]')
def EliteQualifyingSegments = xmlHolder.getNodeValue('//return[1]/ADVEliteQualifyingSegments[1]')
def AccrualMethod = xmlHolder.getNodeValue('//return[1]/AccrualMethod[1]')
def AccrualMethodReason = xmlHolder.getNodeValue('//return[1]/AccrualMethodReason[1]')
