def ActivityBaseEQM = xmlHolder.getNodeValue('//return[1]/BaseEliteQualifyingMiles[1]')
def ActivityBaseEQS = xmlHolder.getNodeValue('//return[1]/BaseEliteQualifyingSegments[1]')
def ActivityBonusEQD = xmlHolder.getNodeValue('//return[1]/BonusEliteQualifyingDollars[1]')
def ActivityBonusEQS = xmlHolder.getNodeValue('//return[1]/BonusEliteQualifyingSegments[1]')
def ActivityBonusEQM = xmlHolder.getNodeValue('//return[1]/BonusEliteQualifyingMiles[1]')
def EliteBonus = xmlHolder.getNodeValue('//return[1]/EliteBonus[1]')
