def EQMMultiplier = xmlHolder.getNodeValue('//return[1]/EQMMultiplier[1]')
def EQSMultiplier = xmlHolder.getNodeValue('//return[1]/EQSMultiplier[1]')

//Get COS/CityPair ID
def sql = Sql.newInstance(url, user, password, driver)
def TxnID = context.expand( '${Request#Request#declare namespace cxf=\'http://cxfws.jee.customerservices.ecdb.americanairlines.com/\'; //cxf:retrieveMemberActivityListDetails[1]/request[1]/transactionId[1]}' )
def Query = ("select T.x_class_of_service,T.x_city_region_id from siebel.s_loy_txn T where T.row_id='"+TxnID+"' ")
def recordset = sql.firstRow(Query)
def x_class_of_service = recordset.x_class_of_service
def x_city_region_id = recordset.x_city_region_id

// Get TXN accrual Values and Multipliers from COS/CityPair Table
sql = Sql.newInstance(url, user, password, driver)
Query = ("select x.attrib_16 as AEMbaseDistance,x.attrib_14 as AEMBonusDistance,x.attrib_17 as AEMBaseRevenue,x.attrib_19 as AEMBonusRevenue,T.x_class_of_service,x.attrib_24 as EQDMultiplier,x.attrib_25 AS EQMMultiplier,x.attrib_23 as EQSMultiplier,M.POINT_TYPE_F_VAL as AEM,T.x_base_miles, T.x_bonus_miles,T.x_method_name,T.X_ELITE_BONUS,T.x_base_eqm, T.x_base_eqd, T.x_base_eqs, T.X_TOTAL_EQM,T.X_TOTAL_EQD,T.X_TOTAL_EQS,T.X_MIN_MILEAGE,T.type_cd as ActivityType,txn_dt as ActivityDate, T.dest_airport_cd, T.orig_airport_cd,T.reason_cd from siebel.s_loy_txn T,siebel.s_loy_member M,siebel.s_org_ext_xm X where M.row_id=T.member_id and X.row_id=T.x_cos_id and x.type='ADV_CLASS_OF_SERVICE' and T.row_id='"+TxnID+"' ")
if(x_class_of_service== null){
	log.info 'Multipliers are taken from Citypair Override table '+x_city_region_id
	Query = ("select x.attrib_16 as AEMbaseDistance,x.attrib_14 as AEMBonusDistance,x.attrib_17 as AEMBaseRevenue,x.attrib_19 as AEMBonusRevenue,T.x_city_region_id,x.attrib_24 as EQDMultiplier,x.attrib_25 AS EQMMultiplier,x.attrib_23 as EQSMultiplier,M.POINT_TYPE_F_VAL as AEM,T.x_base_miles, T.x_bonus_miles,T.x_method_name,T.X_ELITE_BONUS,T.x_base_eqm, T.x_base_eqd, T.x_base_eqs, T.X_TOTAL_EQM,T.X_TOTAL_EQD,T.X_TOTAL_EQS,T.X_MIN_MILEAGE,T.type_cd as ActivityType,txn_dt as ActivityDate, T.dest_airport_cd, T.orig_airport_cd,T.reason_cd from siebel.s_loy_txn T,siebel.s_loy_member M,siebel.s_org_ext_xm X where M.row_id=T.member_id and X.row_id=T.x_city_region_id and x.type='ADV_COS_BKNGCD_OVERRIDE' and T.row_id='"+TxnID+"' ")
}
recordset = sql.firstRow(Query)
