	assert BaseAEMMultiplier.toString() == AEMBaseDistance.toString()
	assert BonusAEMMultiplier.toString() == AEMBonusDistance.toString()
}
else{
	assert BaseAEMMultiplier.toString() == AEMBaseRevenue.toString()
	assert BonusAEMMultiplier.toString() == AEMBonusRevenue.toString()
