def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

def projectPath = groovyUtils.projectPath
log.info projectPath
def Projectname = testRunner.testCase.testSuite.project.name
log.info Projectname
def testSuiteName = testRunner.testCase.testSuite.getName()
log.info testSuiteName
def testCaseName = testRunner.testCase.getName()
log.info testCaseName

//CREATING PROJECT FOLDER
def projectfolder = projectPath+"\\"+Projectname
log.info projectfolder
File newprojectfolder = new File(projectfolder)
if(newprojectfolder.exists()){
	log.info 'Project Folder Already Exists - '+newprojectfolder
}
else{
	newprojectfolder.mkdirs()
	log.info 'Project folder Created - '+newprojectfolder
}

//CREATING DATE FOLDER
def datefolder = new Date().format("yyyy_MM_dd")
log.info datefolder
File newdatefolder = new File(newprojectfolder.toString()+'\\'+datefolder)
if(newdatefolder.exists()){
	log.info 'DATE Folder Already Exists - '+newdatefolder
}
else{
	newdatefolder.mkdirs()
	log.info 'DATE folder Created - '+newdatefolder
}

//CREATING SUITE FOLDER
File suitefolder = new File(newdatefolder.toString()+"\\"+testSuiteName)
if(suitefolder.exists()){
	log.info 'Suite Folder Already Exists - '+suitefolder
}
else{
	suitefolder.mkdirs()
	log.info 'Suite folder Created - '+suitefolder
}
log.info suitefolder

