def full_response = context.expand( '${SOAP Request#Response#declare namespace soapenv=\'http://schemas.xmlsoap.org/soap/envelope/\'; //soapenv:Envelope[1]}' )

def eliteLevel = context.expand( '${DataSource#eliteLevel}' )


//The following two lines is for creating unique filenames. These two needs to be changes for each test case
def req_FileName = testCaseName +eliteLevel+"Request.txt"
def resp_FileName = testCaseName +eliteLevel+"Response.txt"

def file1 = new File(suitefolder.toString() + "/" + req_FileName)
def file2 = new File(suitefolder.toString() + "/" + resp_FileName)
file1.createNewFile()
file2.createNewFile()

//for appending the contents into same file  comment the next 2 lines
file1.write(full_request)
file2.write(full_response)

