//Getting multipliers from COS Table
def marketingCarrierCode = context.expand( '${DataSource#MarketingCarrierCode}' )
def purchBkgCode = context.expand( '${DataSource#PurchBkgCode}' )
StringBuilder query1 = new StringBuilder("");
query1.append("select XM.ROW_ID XM_COS_RID,XM.ATTRIB_03 BOOK_CLASS,XM.ATTRIB_06 COS,NVL(XM.ATTRIB_12,'01-JAN-1990') Eff_Date,   NVL(XM.ATTRIB_13,'31-DEC-2999') Exp_Date,XM.ATTRIB_16 Base_Miles,XM.ATTRIB_14 BONUS,XM.ATTRIB_23 EQS,    XM.ATTRIB_25 EQM,    XM.ATTRIB_17 BASE_REV,XM.ATTRIB_19 BONUS_REV,XM.ATTRIB_53 EQD_REV,XM.ATTRIB_24 EQD_DIST,XM.ATTRIB_15 EQP,NVL(FFP.OU_NUM,'AA') FFP,O.OU_NUM AIRLINE,XM.NAME COS_NAME,XM.TYPE COS_TYPE,O.LOC PAR_ROW_ID_LOC,O_BU.NAME ORG_BU,XM.ATTRIB_43 ACCR_METHOD from SIEBEL.s_org_ext_xm XM,SIEBEL.S_ORG_EXT O,SIEBEL.S_ORG_EXT FFP,SIEBEL.S_BU O_BU where XM.type='ADV_CLASS_OF_SERVICE' AND O.ROW_ID=XM.PAR_ROW_ID AND XM.ATTRIB_48=FFP.ROW_ID(+) AND O_BU.ROW_ID=O.BU_ID AND XM.ATTRIB_13>=SYSDATE AND O.OU_NUM= '")
query1.append(marketingCarrierCode)
query1.append("' and XM.ATTRIB_03= '" )
query1.append(purchBkgCode)
query1.append("' AND XM.ATTRIB_48 is null ")
log.info(query1)
def recordset= SqlInstance.firstRow(query1.toString())
log.info(recordset)
