//Getting multipliers from COS Table
def marketingCarrierCode = context.expand( '${DataSource#MarketingCarrierCode}' )
def purchBkgCode = context.expand( '${DataSource#PurchBkgCode}' )
StringBuilder query1 = new StringBuilder("");
query1.append("select /*+ PARALLEL 32 */ m. mem_num, t.name,mt.end_dt from siebel.s_loy_member m , siebel.s_loy_mem_tier mt, siebel.s_loy_tier t where m.row_id=mt.member_id and mt.tier_id=t.row_id and t.name='Regular' and mt.active_flg='Y' and to_char(end_dt,'YYYY')=to_char(sysdate,'YYYY')+2 and m.status_cd='Active'  order by dbms_random.value")
log.info(query1)

  def recordset= SqlInstance.firstRow(query1.toString())
log.info(recordset)
