	log.info("calSegEQD1:"+calSegEQD1)
	testRunner.testCase.setPropertyValue("calSegEQD1", calSegEQD1.toString())
	def int  calSegEQM=dISTANCE2*EQM
	log.info("calSegEQM:"+calSegEQM)
	testRunner.testCase.setPropertyValue("calSegEQM", calSegEQM.toString())
	def int  calSegEQS=1*EQS
	log.info("calSegEQS:"+calSegEQS)
	testRunner.testCase.setPropertyValue("calSegEQS", calSegEQS.toString())
}

else if  (resAccrualType1=="Distance")
{
	def int  calSegBaseMiles=dISTANCE2*Base_Miles
	log.info("calSegBaseMiles:"+calSegBaseMiles)
	testRunner.testCase.setPropertyValue("calSegBaseMiles", calSegBaseMiles.toString())
	def int  calSegCosBonus=dISTANCE2*BONUS
	log.info("calSegCosBonus:"+calSegCosBonus)
	testRunner.testCase.setPropertyValue("calSegCosBonus", calSegCosBonus.toString())
	def Float  calSegEQD=dISTANCE2*EQD_DIST
	def calSegEQD1=Math.ceil(calSegEQD)
	log.info("calSegEQD1:"+calSegEQD1)
	testRunner.testCase.setPropertyValue("calSegEQD1", calSegEQD1.toString())
	def int  calSegEQM=dISTANCE2*EQM
	log.info("calSegEQM:"+calSegEQM)
	testRunner.testCase.setPropertyValue("calSegEQM", calSegEQM.toString())
	def int  calSegEQS=1*EQS
	log.info("calSegEQS:"+calSegEQS)
	testRunner.testCase.setPropertyValue("calSegEQS", calSegEQS.toString())
}
else 
{
	def int  calSegBaseMiles=0
	log.info("calSegBaseMiles:"+calSegBaseMiles)
