	log.info("calSegBaseMiles:"+calSegBaseMiles)
	testRunner.testCase.setPropertyValue("calSegBaseMiles", calSegBaseMiles.toString())
	def int  calSegCosBonus=resAccrualRevenue2*BONUS_REV
	log.info("calSegCosBonus:"+calSegCosBonus)
	testRunner.testCase.setPropertyValue("calSegCosBonus", calSegCosBonus.toString())
	def float  calSegEQD=resAccrualRevenue2*EQD_REV
	log.info("calSegEQD:"+calSegEQD)
	def calSegEQD1=Math.ceil(calSegEQD)
	log.info("calSegEQD1:"+calSegEQD1)
	testRunner.testCase.setPropertyValue("calSegEQD1", calSegEQD1.toString())
	def int  calSegEQM=dISTANCE2*EQM
	log.info("calSegEQM:"+calSegEQM)
	testRunner.testCase.setPropertyValue("calSegEQM", calSegEQM.toString())
	def int  calSegEQS=1*EQS
	log.info("calSegEQS:"+calSegEQS)
	testRunner.testCase.setPropertyValue("calSegEQS", calSegEQS.toString())
}

else if  (resAccrualType1=="Distance")
{
	def int  calSegBaseMiles=dISTANCE2*Base_Miles
	log.info("calSegBaseMiles:"+calSegBaseMiles)
	testRunner.testCase.setPropertyValue("calSegBaseMiles", calSegBaseMiles.toString())
	def int  calSegCosBonus=dISTANCE2*BONUS
	log.info("calSegCosBonus:"+calSegCosBonus)
	testRunner.testCase.setPropertyValue("calSegCosBonus", calSegCosBonus.toString())
	def Float  calSegEQD=dISTANCE2*EQD_DIST
	def calSegEQD1=Math.ceil(calSegEQD)
	log.info("calSegEQD1:"+calSegEQD1)
	testRunner.testCase.setPropertyValue("calSegEQD1", calSegEQD1.toString())
	def int  calSegEQM=dISTANCE2*EQM
	log.info("calSegEQM:"+calSegEQM)
	testRunner.testCase.setPropertyValue("calSegEQM", calSegEQM.toString())
	def int  calSegEQS=1*EQS
	log.info("calSegEQS:"+calSegEQS)
	testRunner.testCase.setPropertyValue("calSegEQS", calSegEQS.toString())
}
else 
{
	def int  calSegBaseMiles=0
	log.info("calSegBaseMiles:"+calSegBaseMiles)
	testRunner.testCase.setPropertyValue("calSegBaseMiles", calSegBaseMiles.toString())
	def int  calSegCosBonus=0
	log.info("calSegCosBonus:"+calSegCosBonus)
	testRunner.testCase.setPropertyValue("calSegCosBonus", calSegCosBonus.toString())
	def Float  calSegEQD1=0
	log.info("calSegEQD:"+calSegEQD1)
	testRunner.testCase.setPropertyValue("calSegEQD1", calSegEQD1.toString())
	def int  calSegEQM=0
	log.info("calSegEQM:"+calSegEQM)
	testRunner.testCase.setPropertyValue("calSegEQM", calSegEQM.toString())
	def int  calSegEQS=0
	log.info("calSegEQS:"+calSegEQS)
	testRunner.testCase.setPropertyValue("calSegEQS", calSegEQS.toString())
}


//Asserting 
def calSegBaseMiles = context.expand( '${#TestCase#calSegBaseMiles}' )
def calSegCosBonus = context.expand( '${#TestCase#calSegCosBonus}' )
def calSegEQD2 = context.expand( '${#TestCase#calSegEQD1}' )
log.info(calSegEQD2)
def calSegEQD3 =calSegEQD2.toFloat()
def calSegEQM = context.expand( '${#TestCase#calSegEQM}' )
def calSegEQS = context.expand( '${#TestCase#calSegEQS}' )
def ressegEQD1 = context.expand( '${#TestCase#ressegEQD}' )
def ressegEQD2 =ressegEQD1.toFloat()
log.info(ressegEQD2)
assert ressegBaseMiles==calSegBaseMiles
assert ressegCosBonus==calSegCosBonus
assert ressegEQD2==calSegEQD3
assert ressegEQM==calSegEQM
assert ressegEQS==calSegEQS

