con:eventHandlers.con:script:
log.info("Getting the test step status ")

def step = ""

def testCaseStatus ='PASS'
for (testStepResult in  testRunner.getResults() ) {
	def testStep = testStepResult.getTestStep()
	step = "TestStep [ " + testStep.name + " ] finished with status "  + testStepResult.getStatus().toString() + "\n" 
	if(testStepResult.getStatus() == 'PASS') {
		testCaseStatus = 'FAILED'
	}
	log.info("step "+step)
}


log.info("Test case Name "+testRunner.testCase.name+ "Testcase Status "+testCaseStatus)
